INSERT INTO `tbl_product` (`id`, `id_category`, `id_brand`, `name`, `code`, `price`, `promotional_price`, `quantity`, `status`, `description`, `user`, `create_date`, `image`, `level`) VALUES
(44, 16, 7, 'Bluetooth Mozard K8', 'TN001', 500000, 400000, 1000, 'còn hàng', '<h3>Thiết kế đầy c&aacute; t&iacute;nh, 2 phi&ecirc;n bản m&agrave;u xanh - hồng cực kỳ bắt mắt</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tai nghe giá rẻ chính hãng bán tại Thế Giới Di Động\">Tai nghe</a>&nbsp;được l&agrave;m tinh xảo, bộ khung cứng c&aacute;p, c&oacute; hệ thống treo tiện dụng v&agrave; chụp tai phủ b&ecirc;n ngo&agrave;i bởi lớp chất liệu mềm mại, đeo v&agrave;o đầu nhẹ nh&agrave;ng, kh&ocirc;ng l&agrave;m mỏi, đau v&ugrave;ng đầu - cổ - tai.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Kiểu dáng hiện đại, màu sắc thời thượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-1.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Kiểu dáng hiện đại, màu sắc thời thượng\" /></a></p>\r\n\r\n<p>C&oacute; thể chỉnh&nbsp;<strong>k&eacute;o gi&atilde;n khung tai nghe đến 3.7 cm</strong>, dễ d&agrave;ng t&ugrave;y chỉnh&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-mozard\" target=\"_blank\" title=\"Tai nghe Mozard chính hãng, giá rẻ, bán tại Thế Giới Di Động\">tai nghe Mozard</a>&nbsp;ph&ugrave; hợp với cỡ đầu của bạn v&agrave; đem đến cảm gi&aacute;c sử dụng thoải m&aacute;i nhất.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8--8.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Độ giãn gọng tai nghe đến mức 3.7 cm.\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8--8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8--8.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Độ giãn gọng tai nghe đến mức 3.7 cm.\" /></a></p>\r\n\r\n<h3>Đệm tai bao bọc quanh tai nghe d&agrave;y dặn n&ecirc;n đeo rất &ecirc;m &aacute;i, dễ chịu</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Thiết kế phần đệm tai được làm dày, mềm\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-2.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Thiết kế phần đệm tai được làm dày, mềm\" /></a></p>\r\n\r\n<h3>Kết nối dạng kh&ocirc;ng d&acirc;y qua&nbsp;Bluetooth 4.2 hoặc c&oacute; d&acirc;y qua cổng 3.5 mm</h3>\r\n\r\n<p>C&ocirc;ng nghệ<strong>&nbsp;Bluetooth 4.2&nbsp;</strong>cho kết nối nhanh, khoảng c&aacute;ch kết nối xa đến&nbsp;<strong>10 m</strong>. Nếu th&iacute;ch kiểu kết nối thiết bị c&oacute; d&acirc;y truyền thống, bạn c&oacute; thể sử dụng th&ecirc;m d&acirc;y c&aacute;p c&oacute; cổng 3.5 để nối v&agrave;o.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Kết nối nhanh chóng qua đường truyền không dây Bluetooth 4.2\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-3.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Kết nối nhanh chóng qua đường truyền không dây Bluetooth 4.2\" /></a></p>\r\n\r\n<h3>Chất &acirc;m g&acirc;y ngạc nhi&ecirc;n với độ r&otilde; n&eacute;t cao, &acirc;m bass s&acirc;u, khỏe</h3>\r\n\r\n<p>T&aacute;i hiện<strong>&nbsp;&acirc;m thanh chi tiết, r&otilde; r&agrave;ng</strong>&nbsp;nhờ c&oacute; driver lớn, thiết kế c&aacute;ch &acirc;m, cho người d&ugrave;ng dễ d&agrave;ng ho&agrave;n to&agrave;n ch&igrave;m đắm v&agrave;o giai điệu du dương của nhạc trữ t&igrave;nh, cuốn theo tiết tấu của c&aacute;c b&agrave;i rap, rock mạnh mẽ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-5.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Cả thế giới âm nhạc bùng nổ bên tai với âm thanh sống động, trung thực\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-5.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Cả thế giới âm nhạc bùng nổ bên tai với âm thanh sống động, trung thực\" /></a></p>\r\n\r\n<p>C&oacute; microphone cho&nbsp;<strong>giọng n&oacute;i trong trẻo</strong>, nghe v&agrave; n&oacute;i dễ d&agrave;ng khi chơi game, đ&agrave;m thoại.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Có tích hợp mic thoại\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-4.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Có tích hợp mic thoại\" /></a></p>\r\n\r\n<h3>Dung lượng pin&nbsp;300 mAh cung cấp 15 giờ sử dụng, sạc khoảng 2 giờ với cổng Micro USB quen thuộc</h3>\r\n\r\n<p>Mozard K8 mang đến thời gian trải nghiệm d&agrave;i, y&ecirc;n t&acirc;m hơn khi chọn&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tham khảo tai nghe Bluetooth kinh doanh tại Thế Giới Di Động\">tai nghe Bluetooth</a>&nbsp;đồng h&agrave;nh trong c&aacute;c chuyến đi xa, nhất l&agrave; đến những nơi kh&ocirc;ng c&oacute; điện.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-6.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Trải nghiệm tùy thích với thời gian dùng đến 15 giờ chỉ qua 1 lần sạc đầy\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-6.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Trải nghiệm tùy thích với thời gian dùng đến 15 giờ chỉ qua 1 lần sạc đầy\" /></a></p>\r\n\r\n<h3>Sử dụng n&uacute;t nhấn điều khiển trực quan tiện dụng</h3>\r\n\r\n<p>-&nbsp;<strong>N&uacute;t nguồn:</strong>&nbsp;Bật/Tắt tai nghe, kết nối Bluetooth.</p>\r\n\r\n<p>-&nbsp;<strong>N&uacute;t k&yacute; hiệu +</strong>: Tăng &acirc;m lượng.</p>\r\n\r\n<p>-&nbsp;<strong>N&uacute;t k&yacute; hiệu -:</strong>&nbsp;Giảm &acirc;m lượng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-7.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth Mozard K8 - Điều khiển nút nhấn thân thiện\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/235955/bluetooth-mozard-k8-7.jpg\" title=\"Tai nghe Bluetooth Mozard K8 - Điều khiển nút nhấn thân thiện\" /></a></p>\r\n\r\n<p>Thu h&uacute;t với thiết kế của&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/bluetooth-mozard-k8\" target=\"_blank\" title=\" tai nghe Bluetooth Mozard K8\">tai nghe Bluetooth Mozard K8</a>&nbsp;nhưng khi d&ugrave;ng thử, bạn sẽ m&ecirc; ngay chất &acirc;m, khả năng kết nối vượt trội m&agrave; tai nghe mang lại. Nếu y&ecirc;u, đừng chần chừ nhấn n&uacute;t MUA NGAY để sở hữu ngay một chiếc cho ri&ecirc;ng m&igrave;nh.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/bluetooth-mozard-k8-xanh-1-1.jpeg', 'hot'),
(45, 16, 7, 'EP Mozard DS510 WB', 'TN002', 150000, 120000, 500, 'còn hàng', '<h3>Được thiết kế gọn g&agrave;ng, bền bỉ, sử dụng được với nhiều loại thiết bị, tiện dụng mang theo mọi l&uacute;c mọi nơi.</h3>\r\n\r\n<p>&bull;&nbsp;Thiết kế nhỏ gọn, m&agrave;u sắc thời thượng,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo tai nghe đang bán tại Thế Giới Di Động\">tai nghe</a>&nbsp;c&oacute; thể cuộn lại dễ d&agrave;ng gi&uacute;p hạn chế rối d&acirc;y khi cất giữ.</p>\r\n\r\n<p>&bull;&nbsp;Chế t&aacute;c từ chất liệu bền bỉ, cho thời gian sử dụng l&acirc;u d&agrave;i.</p>\r\n\r\n<p>&bull;&nbsp;Kh&ocirc;ng chỉ nghe nhạc,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-mozard\" target=\"_blank\" title=\"Tham  khảo tai nghe Mozard đang bán tại Thế Giới Di Động\">tai nghe Mozard</a>&nbsp;n&agrave;y c&ograve;n được trang bị mic thoại tiện lợi, hỗ trợ thu &acirc;m đầy đủ, chi tiết.</p>\r\n\r\n<p>&bull;&nbsp;C&aacute;c n&uacute;t điều khiển được thiết kế ngay tr&ecirc;n phần d&acirc;y tai nghe, dễ d&agrave;ng t&ugrave;y chỉnh c&aacute;c chức năng cơ bản như:&nbsp;Trả lời cuộc gọi, tăng/giảm &acirc;m lượng.</p>\r\n\r\n<p>&bull;&nbsp;Tương th&iacute;ch với c&aacute;c thiết bị sử dụng hệ điều h&agrave;nh Android, iOS, Windows, macOS</p>\r\n', 'admin', '01/12/2022', 'public/uploads/tai-nghe-ep-mozard-ds510-wb-xanh-up-1-org.jpg', 'normal'),
(46, 16, 7, 'Bluetooth True Wireless ENCO Air', 'TN003', 1990000, 1790000, 995, 'còn hàng', '<h3>l&agrave; chiếc tai nghe mới được ra mắt của nh&agrave; OPPO v&agrave;o th&aacute;ng 8/2022, chỉ v&agrave;i th&aacute;ng sau khi phi&ecirc;n bản tiền nhiệm của n&oacute; l&agrave; OPPO ENCO Air 2 ra đời. Đến với phi&ecirc;n bản Pro n&agrave;y, nh&agrave; OPPO đ&atilde; c&oacute; v&agrave;i n&acirc;ng cấp m&agrave; m&igrave;nh rất th&iacute;ch. Vậy những điểm thu h&uacute;t đ&oacute; l&agrave; g&igrave;?</h3>\r\n\r\n<h3>Thiết kế gọn g&agrave;ng, kiểu d&aacute;ng sang trọng, m&agrave;u sắc trang nh&atilde;</h3>\r\n\r\n<p>Thay v&igrave; thiết kế dạng nắp hộp trong suốt như trước kia, chiếc tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 n&agrave;y mang đến thiết kế hộp đựng được bao k&iacute;n ho&agrave;n to&agrave;n tạo cảm gi&aacute;c chắc chắn cho hộp đựng. C&aacute;c chi tiết thiết kế như bản lề, đường bo g&oacute;c m&igrave;nh nhận thấy chiếc ENCO Air 2 Pro n&agrave;y vẫn được chăm ch&uacute;t tỉ mỉ. B&ecirc;n ngo&agrave;i hộp sạc sẽ gồm c&oacute;:</p>\r\n\r\n<p>- Đ&egrave;n b&aacute;o pin ngay ph&iacute;a dưới nắp hộp c&oacute; thể hiển thị 3 m&agrave;u sắc trạng th&aacute;i pin:&nbsp;Đỏ (Pin yếu, sắp hết pin) - V&agrave;ng (Mức pin vừa) - Xanh l&aacute; (Pin ở trạng th&aacute;i đầy). Cụ thể, khi mở nắp hộp ra (đảm bảo tai nghe nằm trong hộp sạc), m&agrave;u đ&egrave;n b&aacute;o sẽ thể hiện lượng pin c&ograve;n lại của tai nghe. Khi đ&oacute;ng hộp sạc lại m&agrave;u đ&egrave;n b&aacute;o sẽ thể hiện lượng pin c&ograve;n lại của hộp sạc.</p>\r\n\r\n<p>- Khe cắm sạc cho hộp đựng.</p>\r\n\r\n<p>- Mặt sau c&oacute; t&ecirc;n nh&atilde;n hiệu tr&ocirc;ng th&igrave; đơn giản nhưng lại v&ocirc; c&ugrave;ng đẹp mắt.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-2-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Thiết kế gọn đẹp, sang trọng\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-2-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-2-4.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Thiết kế gọn đẹp, sang trọng\" /></a></p>\r\n\r\n<p>Phần bản lề kh&aacute; chắc chắn, đ&oacute;ng mở dễ d&agrave;ng chỉ bằng một tay. Ngo&agrave;i ra, m&igrave;nh c&oacute; thể nghe được tiếng &quot;t&aacute;ch&quot; khi đ&oacute;ng hộp.&nbsp;</p>\r\n\r\n<p>Tổng thể chiếc hộp sạc, m&igrave;nh chỉ c&oacute; 2 từ &ldquo;tuyệt vời&rdquo; để diễn tả: Kiểu d&aacute;ng đẹp mắt, m&agrave;u sắc nh&atilde; nhặn, thiết kế bo tr&ograve;n gọn nhẹ dễ cầm, dễ nắm trong l&ograve;ng b&agrave;n tay. Với thiết kế n&agrave;y, m&igrave;nh ho&agrave;n to&agrave;n c&oacute; thể bỏ t&uacute;i &aacute;o hoặc balo v&agrave; mang đi bất k&igrave; đ&acirc;u, v&ocirc; c&ugrave;ng tiện lợi.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, phần hộp được l&agrave;m từ nhựa b&oacute;ng, đẹp th&igrave; c&oacute; đẹp, nhưng nếu cầm nắm nhiều c&oacute; thể sẽ b&aacute;m d&iacute;nh một ch&uacute;t dấu v&acirc;n tay. Nếu muốn bảo vệ tốt hơn cho hộp sạc bạn c&oacute; thể trang bị th&ecirc;m case đựng chuy&ecirc;n dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-9-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Làm từ nhựa bóng thời thượng\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-9-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-9-1.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Làm từ nhựa bóng thời thượng\" /></a></p>\r\n\r\n<p>Về phần tai nghe, khoan h&atilde;y n&oacute;i về chất &acirc;m v&agrave; c&aacute;c yếu tố trải nghiệm kh&aacute;c, chỉ ri&ecirc;ng phần thiết kế tai nghe m&igrave;nh đ&atilde; rất h&agrave;i l&ograve;ng. Lần n&agrave;y, OPPO mang đến một chiếc&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-in-ear\" target=\"_blank\" title=\"Tham khảo các loại tai nghe In-ear đang bán trên thegioididong.com\">tai nghe In-ear</a>&nbsp;rất &ldquo;vừa l&ograve;ng&rdquo; những con d&acirc;n chỉ hợp với tai nghe dạng In-ear như m&igrave;nh.</p>\r\n\r\n<p>Phần tai nghe cơ bản về k&iacute;ch thước, khối lượng vẫn tương tự như chiếc&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/bluetooth-true-wireless-oppo-enco-air-2-ete11\" target=\"_blank\" title=\"Tham khảo tai nghe OPPO ENCO Air 2 đang bán tại thegioididong.com\">ENCO Air 2</a>. Nghĩa l&agrave; khối lượng vẫn gọn nhẹ, phần housing theo m&igrave;nh l&agrave; vừa phải, tuy nhi&ecirc;n ở phi&ecirc;n bản Pro n&agrave;y, c&oacute; th&ecirc;m phần n&uacute;m đệm tai từ chất liệu Silicone vừa bền bỉ lại dẻo dai, mềm mại gi&uacute;p tai nghe vừa vặn ho&agrave;n to&agrave;n với cấu tạo tai mỗi người, đeo l&acirc;u m&agrave; vẫn cảm thấy thoải m&aacute;i, kh&ocirc;ng hề đau tai ch&uacute;t n&agrave;o.</p>\r\n\r\n<p>M&igrave;nh đ&atilde; sử dụng tai nghe trong l&uacute;c ăn, đi bộ, thậm ch&iacute; l&agrave; chạy bộ trong thời gian d&agrave;i th&igrave; cảm gi&aacute;c đeo tai nghe vẫn &ecirc;m &aacute;i v&agrave; b&aacute;m chắc tr&ecirc;n tai, kh&ocirc;ng hề c&oacute; dấu hiệu rơi xuống. Đ&acirc;y l&agrave; một điểm quan trọng m&agrave; m&igrave;nh nghĩ bất kể ai cũng đều cần v&agrave; quan t&acirc;m khi chọn mua tai nghe.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-8-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless OPPO ENCO Air 2 Pro ETE21 - Tai nghe In-ear với miếng đệm tai mềm mại, êm ái\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-8-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-8-2.jpg\" title=\"Tai nghe Bluetooth True Wireless OPPO ENCO Air 2 Pro ETE21 - Tai nghe In-ear với miếng đệm tai mềm mại, êm ái\" /></a></p>\r\n\r\n<p>Đặc biệt, khi m&igrave;nh vừa bỏ tai nghe v&agrave;o hộp sạc phần đ&iacute;nh h&uacute;t nam ch&acirc;m ở dưới ch&acirc;n tai nghe gi&uacute;p tai nghe vừa khớp với hộp sạc. Ngay khi đ&oacute;ng nắp hộp sạc, tai nghe sẽ về trạng th&aacute;i sạc kh&ocirc;ng d&acirc;y. Như vậy, mỗi khi kh&ocirc;ng cần sử dụng tai nghe nữa, chỉ cần bỏ tai nghe v&agrave;o hộp sạc th&igrave; tai nghe sẽ vừa được bảo vệ vừa đảm bảo pin lu&ocirc;n ở trạng th&aacute;i đầy gi&uacute;p n&acirc;ng cao trải nghiệm tối ưu cho lần sử dụng tiếp theo.</p>\r\n\r\n<h3>&Acirc;m thanh sống động, dải &acirc;m r&otilde; r&agrave;ng, chi tiết</h3>\r\n\r\n<p>Ti&ecirc;u ch&iacute; tiếp theo v&ocirc; c&ugrave;ng quan trọng, đ&oacute; l&agrave; chất &acirc;m ph&aacute;t ra từ tai nghe. Theo m&igrave;nh cảm nhận &acirc;m thanh kh&aacute; r&otilde; r&agrave;ng, khi m&igrave;nh nghe những bản nhạc c&oacute; nhiều &acirc;m bass như EDM, m&igrave;nh c&oacute; thể nghe được từng nhịp trống, &acirc;m bass gọn g&agrave;ng, chắc khoẻ chứ kh&ocirc;ng hề l&agrave;m mất đi độ chi tiết của &acirc;m trầm.</p>\r\n\r\n<p>Với khả năng n&agrave;y, m&igrave;nh xem những bộ phim bom tấn với nhiều t&igrave;nh tiết dồn dập cũng kh&ocirc;ng phải vấn đề. So với chiếc ENCO Air 2 trước đ&oacute; th&igrave; phi&ecirc;n bản Pro n&agrave;y m&igrave;nh đ&aacute;nh gi&aacute; l&agrave; cho khả năng hiển thị nhiều &acirc;m bass hơn hẳn, &acirc;m bass cũng d&agrave;y v&agrave; chắc khỏe hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-5-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Chất âm đầy đủ, chi tiết\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-5-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-5-2.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Chất âm đầy đủ, chi tiết\" /></a></p>\r\n\r\n<p>Khi sử dụng chung với app, m&igrave;nh c&oacute; thể điều chỉnh linh hoạt với 3 bộ chỉnh &acirc;m. Tuy nhi&ecirc;n, &acirc;m thanh từ 3 bộ chỉnh &acirc;m n&agrave;y, theo m&igrave;nh cảm nhận th&igrave; kh&ocirc;ng c&oacute; nhiều sự kh&aacute;c biệt lắm. C&oacute; một chế độ đặc biệt đ&oacute; l&agrave; Game Mode, khi k&iacute;ch hoạt chế độ n&agrave;y sẽ cho ph&eacute;p &acirc;m thanh truyền đến nhanh ch&oacute;ng, mượt m&agrave;, đưa độ trễ gần như về con số 0 gi&uacute;p hỗ trợ tối đa trong qu&aacute; tr&igrave;nh chơi game.</p>\r\n\r\n<p>Một điểm đ&aacute;ng ch&uacute; &yacute; nữa l&agrave; chiếc tai nghe OPPO n&agrave;y c&oacute; t&iacute;ch hợp khả năng chống ồn. Trong qu&aacute; tr&igrave;nh nghe nhạc, nếu kh&ocirc;ng muốn tạp &acirc;m ở m&ocirc;i trường th&igrave; c&oacute; thể chuyển qua chế độ Noise Canceling với khả năng lọc hiệu quả khoảng 70% tiếng ồn từ b&ecirc;n ngo&agrave;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-11-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Lọc tiếng ồn hiệu quả\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-11-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-11-2.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Lọc tiếng ồn hiệu quả\" /></a></p>\r\n\r\n<h3>Hỗ trợ mic đ&agrave;m thoại tiện dụng</h3>\r\n\r\n<p>B&ecirc;n cạnh &acirc;m thanh r&otilde; r&agrave;ng, ch&acirc;n thực th&igrave; tr&ecirc;n mỗi chiếc tai nghe đều được gắn micro đ&agrave;m thoại cho chất lượng &acirc;m thanh thu được r&otilde; r&agrave;ng, mượt m&agrave;. M&igrave;nh c&oacute; thể thoải m&aacute;i nghe, gọi thực hiện c&aacute;c cuộc gọi qua tai nghe chỉ bằng c&aacute;ch nhấn đ&uacute;p v&agrave;o 1 b&ecirc;n tai nghe khi c&oacute; cuộc gọi thoại tới.</p>\r\n\r\n<p>Ngo&agrave;i ra,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo các loại tai nghe đang bán trên thegioididong.com\">tai nghe</a>&nbsp;được trang bị khả năng khử ồn AI gi&uacute;p loại bỏ tạp &acirc;m từ m&ocirc;i trường như tiếng gi&oacute;, tiếng ồn xa cho chất lượng &acirc;m thanh trong trẻo hơn. Tuy nhi&ecirc;n, với những v&ugrave;ng &acirc;m thanh qu&aacute; ồn &agrave;o như tiếng c&ograve;i xe khi đứng trực tiếp ở ngo&agrave;i đường th&igrave; khả năng lọc &acirc;m n&agrave;y kh&ocirc;ng qu&aacute; hiệu quả. Nhưng với những g&igrave; m&agrave; chiếc tai nghe n&agrave;y đ&atilde; thể hiện v&agrave; tầm gi&aacute; của n&oacute;, m&igrave;nh thấy như vậy l&agrave; qu&aacute; hợp l&yacute; rồi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-1-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless OPPO ENCO Air 2 Pro ETE21 - Hỗ trợ mic đảm thoại chuyên dụng\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-1-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-1-4.jpg\" title=\"Tai nghe Bluetooth True Wireless OPPO ENCO Air 2 Pro ETE21 - Hỗ trợ mic đảm thoại chuyên dụng\" /></a></p>\r\n\r\n<h3>Cảm ứng nhanh nhạy, điều khiển dễ d&agrave;ng, chức năng đa dạng</h3>\r\n\r\n<p>Phần điều cảm ứng được t&iacute;ch hợp ở cả 2 b&ecirc;n tai, bạn c&oacute; thể điều khiển tai nghe dễ d&agrave;ng bằng c&aacute;c thao t&aacute;c chạm, vuốt v&ocirc; c&ugrave;ng mượt m&agrave;:</p>\r\n\r\n<p>- Khi nhạc dừng: Chạm 2 lần để chuyển sang chế độ ph&aacute;t.</p>\r\n\r\n<p>- Khi nhạc đang ph&aacute;t: Chạm 2 lần để chuyển sang b&agrave;i nhạc kế tiếp.</p>\r\n\r\n<p>- Khi c&oacute; cuộc gọi tới: Chạm 2 lần để nhận cuộc gọi.</p>\r\n\r\n<p>- Nhấn giữ để linh động chuyển qua lại 2 chế độ Noise Canceling v&agrave; Transparency.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-3-3.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless OPPO ENCO Air 2 Pro ETE21 - Cảm ứng chạm vuốt mượt mà, đa dạng tính năng\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-3-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-3-3.jpg\" title=\"Tai nghe Bluetooth True Wireless OPPO ENCO Air 2 Pro ETE21 - Cảm ứng chạm vuốt mượt mà, đa dạng tính năng\" /></a></p>\r\n\r\n<p>Đặc biệt tai nghe c&oacute; thể ph&acirc;n t&iacute;ch, ph&aacute;t hiện v&agrave; tự động dừng ph&aacute;t nhạc khi bạn th&aacute;o tai nghe ra khỏi tai v&ocirc; c&ugrave;ng hiện đại. Khi bạn đeo lại cả 2 tai th&igrave; nhạc sẽ tự động được ph&aacute;t lại. B&ecirc;n cạnh đ&oacute;, tai nghe n&agrave;y c&ograve;n c&oacute; thể chụp ảnh nhanh (Hỗ trợ OPPO ColorOS 11.3 trở l&ecirc;n).</p>\r\n\r\n<p>Ngo&agrave;i điều khiển trực tiếp tr&ecirc;n tai nghe, bạn c&ograve;n c&oacute; thể điều khiển nhiều chức năng hơn khi tải app HeyMelody về m&aacute;y.</p>\r\n\r\n<p>Link tải app HeyMelody:&nbsp;<a href=\"https://play.google.com/store/apps/details?id=com.heytap.headset&amp;hl=vi&amp;gl=US\" target=\"_blank\" title=\"Tải app HeyMelody cho điện thoại Android\">Android</a>&nbsp;|&nbsp;<a href=\"https://apps.apple.com/vn/app/heymelody/id1564626893\" target=\"_blank\" title=\"Tải app HeyMelody cho điện thoại iPhone\">iOS</a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe-khong-day\" target=\"_blank\" title=\"Tham khảo các loại tai nghe sử dụng công nghệ Bluetooth đang bán trên thegioididong.com\">C&ocirc;ng nghệ Bluetooth 5.2</a>&nbsp;hiện đại cho kết nối mượt m&agrave;, ổn định</h3>\r\n\r\n<p>Với lần kết nối đầu ti&ecirc;n, bạn cần thực hiện c&aacute;c thao t&aacute;c như d&ograve;, t&igrave;m tai nghe v&agrave; nhấn &ldquo;Connect&rdquo; để kết nối tai nghe v&agrave; thiết bị. Khi gh&eacute;p nối th&agrave;nh c&ocirc;ng, OPPO ENCO Air 2 Pro c&oacute; thể ghi nhớ thiết bị v&agrave; tự động kết nối từ lần thứ 2 trở đi ngay khi bạn mở hộp sạc (bạn cần đảm bảo thiết bị gh&eacute;p nối đ&atilde; mở sẵn Bluetooth nh&eacute;).</p>\r\n\r\n<p>Nếu tai nghe đ&atilde; kết nối với thiết bị kh&aacute;c trước đ&oacute; nhưng kh&ocirc;ng thể huỷ kết nối từ thiết bị kia th&igrave; bạn c&oacute; thể thiết lập lại kết nối của tai nghe bằng c&aacute;ch nhấn giữ tai nghe trong khoảng 15 gi&acirc;y (đến khi nghe được từ &quot;Pairing&quot; l&agrave; được). Sau đ&oacute;, bạn c&oacute; thể &aacute;p dụng c&aacute;ch kết nối cho lần đầu như hướng dẫn tr&ecirc;n một c&aacute;ch b&igrave;nh thường.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-7-4.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Đường truyền mượt mà, ổn định với công nghệ Bluetooth 5.2\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-7-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-7-4.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Đường truyền mượt mà, ổn định với công nghệ Bluetooth 5.2\" /></a></p>\r\n\r\n<p>Do sử dụng c&ocirc;ng nghệ Bluetooth 5.2 n&ecirc;n khả năng kết nối m&igrave;nh đ&aacute;nh gi&aacute; l&agrave; kh&aacute; nhanh ch&oacute;ng v&agrave; đường truyền kết nối ổn định cho &acirc;m thanh ph&aacute;t ra mượt m&agrave;. Khoảng c&aacute;ch kết nối tối ưu m&agrave; nh&agrave; sản xuất khuyến nghị l&agrave; trong v&ograve;ng 10 m, tuy nhi&ecirc;n, thực tế m&igrave;nh đo được khoảng c&aacute;ch kết nối c&oacute; thể d&agrave;i hơn một ch&uacute;t v&agrave; vẫn mượt m&agrave;, miễn bạn đảm bảo giữa thiết bị v&agrave; tai nghe kh&ocirc;ng c&oacute; c&oacute; vật cản l&agrave; được.&nbsp;</p>\r\n\r\n<p>Điểm tiện dụng của khả năng kết nối Bluetooth ch&iacute;nh l&agrave; bạn c&oacute; thể thoải m&aacute;i vừa nghe nhạc, vừa di chuyển khắp ph&ograve;ng m&agrave; kh&ocirc;ng cần l&uacute;c n&agrave;o cũng phải cầm theo thiết bị như c&aacute;c d&ograve;ng tai nghe c&oacute; d&acirc;y kh&aacute;c. Nhưng c&oacute; một nhược điểm m&agrave; bất kể tai nghe True Wireless n&agrave;o cũng gặp đ&oacute; l&agrave; khi gặp vật cản như bức tường, c&aacute;nh cửa th&igrave; kết nối c&oacute; thể bị gi&aacute;n đoạn v&agrave; xuất hiện hiện tượng giật, lag thậm ch&iacute; mất kết nối. Tuy nhi&ecirc;n, với nhu cầu sử dụng của m&igrave;nh th&igrave; khả năng kết nối Bluetooth l&agrave; v&ocirc; c&ugrave;ng tiện dụng v&agrave; cần thiết, việc đảm bảo giữa khoảng c&aacute;ch kết nối kh&ocirc;ng gặp vật cản với m&igrave;nh kh&ocirc;ng phải l&agrave; vấn đề.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-10-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Thoải mái di chuyển trong phạm vi 10 m\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-10-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-10-2.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Thoải mái di chuyển trong phạm vi 10 m\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe-chong-nuoc\" target=\"_blank\" title=\"Tham khảo các loại tai nghe chống nước đang bán trên thegioididong.com\">Chống nước</a>&nbsp;chuẩn IP54</h3>\r\n\r\n<p>Theo m&igrave;nh đ&acirc;y l&agrave; một t&iacute;nh năng v&ocirc; c&ugrave;ng tiện lợi đặc biệt với m&igrave;nh hoặc những bạn thường xuy&ecirc;n ra ngo&agrave;i c&oacute; thể gặp thời tiết xấu như những cơn mưa bất thường, m&ocirc;i trường bụi bẩn hoặc những bạn d&ugrave;ng để đi tập thể dục, thể thao lu&ocirc;n cần một chiếc tai nghe c&oacute; thể chống nước, chống mồ h&ocirc;i hiệu quả.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/tai-nghe-bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-160922-065706.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Chống nước chuẩn IP54\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/tai-nghe-bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-160922-065706.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/tai-nghe-bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-160922-065706.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Chống nước chuẩn IP54\" /></a></p>\r\n\r\n<h3>Thời gian sử dụng, cổng sạc Type-C</h3>\r\n\r\n<p>Nếu bạn đang băn khoăn về thời gian sử dụng của một chiếc tai nghe Bluetooth thường chỉ tầm 4 - 5 tiếng th&igrave; đừng lo lắng nữa, chiếc tai nghe OPPO ENCO Air 2 Pro c&oacute; thể cho thời gian sử dụng li&ecirc;n tục tới 6 - 7 tiếng. M&igrave;nh đ&atilde; thử sử dụng nghe nhạc, xem phim li&ecirc;n tục v&agrave; kết quả l&agrave; thời gian sử dụng n&agrave;y thực sự khiến m&igrave;nh ấn tượng. Nếu bạn d&ugrave;ng chung với hộp sạc th&igrave; thời gian sử dụng sẽ c&ograve;n l&acirc;u nữa, c&oacute; thể đạt đến 2- 3 ng&agrave;y với mức sử dụng b&igrave;nh thường.</p>\r\n\r\n<p>Theo th&ocirc;ng số từ nh&agrave; sản xuất, khi sử dụng k&egrave;m với hộp sạc, k&eacute;o d&agrave;i thời gian l&ecirc;n tới 28 tiếng v&agrave; chỉ mất 2 tiếng để sạc đầy hộp sạc qua cổng Type-C th&ocirc;ng dụng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-6-2.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Sạc pin tiện lợi\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-6-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/286335/bluetooth-true-wireless-oppo-enco-air-2-pro-ete21-6-2.jpg\" title=\"Tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 - Sạc pin tiện lợi\" /></a></p>\r\n\r\n<p>T&oacute;m lại, tai nghe Bluetooth TWS OPPO ENCO Air 2 Pro ETE21 thực sự ph&ugrave; hợp với những ai mong muốn t&igrave;m kiếm một d&ograve;ng tai nghe sở hữu thiết kế nhỏ gọn, kiểu d&aacute;ng sang chảnh t&iacute;ch hợp nhiều c&ocirc;ng nghệ hiện đại như: Chống ồn, Bluetooth 5.2, chống nước chuẩn IP54,...</p>\r\n', 'admin', '01/12/2022', 'public/uploads/bluetooth-true-wireless-oppo-enco-air-eti61-trang-4-1.jpg', 'hot'),
(47, 16, 8, 'Bluetooth True Wireless AVA DS200A WB', 'TN004', 690000, 375000, 499, 'còn hàng', '<h3>Thiết kế tối giản, m&agrave;u sắc th&acirc;n thuộc&nbsp;</h3>\r\n\r\n<p>Phần tai nghe được thiết kế nhỏ gọn, khối lượng nhẹ kết hợp n&uacute;m tai silicon vừa khớp khoang tai cho cảm gi&aacute;c đeo&nbsp;<strong>&ecirc;m &aacute;i, dễ chịu</strong>. Đi k&egrave;m với 2 t&ocirc;ng m&agrave;u l&agrave;&nbsp;<strong>xanh navy v&agrave; đen</strong>&nbsp;th&acirc;n quen, trẻ trung ph&ugrave; hợp sử dụng trong mọi ngữ cảnh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112606.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế nhỏ gọn, tối giản, trẻ trung - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112606.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112606.jpg\" title=\"Thiết kế nhỏ gọn, tối giản, trẻ trung - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-khong-day\" target=\"_blank\" title=\"Tham khảo thêm tai nghe True Wireless tại Thế Giới Di Động\">Tai nghe True Wireless</a>&nbsp;n&agrave;y đi c&ugrave;ng hộp sạc&nbsp;được chế t&aacute;c gọn g&agrave;ng, cẩn trọng trong từng đường n&eacute;t tạo n&ecirc;n vẻ sang trọng, c&oacute; bề mặt với diện t&iacute;ch tiếp x&uacute;c rộng gi&uacute;p vững v&agrave;ng khi đặt tr&ecirc;n mặt b&agrave;n, kệ tủ.&nbsp;</p>\r\n\r\n<h3>Tai nghe Bluetooth True Wireless AVA+ DS200A-WB cho chất &acirc;m mạnh mẽ, r&otilde; n&eacute;t&nbsp;</h3>\r\n\r\n<p>Ngo&agrave;i chức năng ph&aacute;t &acirc;m thanh sống động, ch&acirc;n thực tạo b&ugrave;ng nổ trong từng nốt &acirc;m, tai nghe c&ograve;n được trang bị micro đ&agrave;m thoại cho giọng n&oacute;i r&otilde; r&agrave;ng, dễ nghe khi đối thoại cuộc gọi với người th&acirc;n, bạn b&egrave;, đối t&aacute;c.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-081121-060144.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe cho âm thanh mạnh mẽ thỏa cảm xúc - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-081121-060144.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-081121-060144.jpg\" title=\"Tai nghe cho âm thanh mạnh mẽ thỏa cảm xúc - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/tai-nghe-ava\" target=\"_blank\" title=\"Tham khảo thêm Tai nghe AVA+ tại Thế Giới Di Động\">Tai nghe AVA+</a>&nbsp;cho 4 giờ sử dụng, kết hợp c&ugrave;ng với hộp sạc cho tới 12 giờ d&ugrave;ng v&agrave; mất 1.5 giờ để nạp đầy qua cổng Micro USB</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112609.jpg\" onclick=\"return false;\"><img alt=\"Tổng thời gian sử dụng lên đến 12 giờ - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112609.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112609.jpg\" title=\"Tổng thời gian sử dụng lên đến 12 giờ - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" /></a></p>\r\n\r\n<h3>Sử dụng ổn định c&ugrave;ng chuẩn Bluetooth 5.0, tương th&iacute;ch với hầu hết hệ điều h&agrave;nh hiện c&oacute;</h3>\r\n\r\n<p>Kết nối Bluetooth 5.0 cho ph&eacute;p kết nối&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo thêm tai nghe tại Thế Giới Di Động\">tai nghe</a>&nbsp;với đa dạng c&aacute;c&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo thêm điện thoại tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop-ldp\" target=\"_blank\" title=\"Tham khảo thêm laptop tại Thế Giới Di Động\">laptop</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo thêm tablet tại Thế Giới Di Động\">tablet</a>&nbsp;thuộc nhiều hệ điều h&agrave;nh kh&aacute;c nhau như Android, iOS, Windows, macOS. Chuẩn kết nối thế hệ mới n&agrave;y giữ được&nbsp;<strong>độ ổn định cao, tr&aacute;nh giật lag</strong>&nbsp;tối ưu nhất trong phạm vi l&ecirc;n đến&nbsp;<strong>10 m</strong>.&nbsp;</p>\r\n\r\n<p><strong>Tham khảo:</strong>&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/bluetooth-50-chuan-bluetooth-danh-cho-thoi-dai-kho-1113891\" target=\"_blank\" title=\"Chuẩn kết nối Bluetooth 5.0 nổi bật - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\">Bluetooth 5.0 l&agrave; g&igrave;? C&oacute; đặc điểm g&igrave; nổi bật? Đem lại lợi &iacute;ch g&igrave;?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-041121-124756.jpg\" onclick=\"return false;\"><img alt=\"Chuẩn kết nối Bluetooth 5.0 ổn định, đường truyền xa lên đến 10 m - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-041121-124756.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-041121-124756.jpg\" title=\"Chuẩn kết nối Bluetooth 5.0 ổn định, đường truyền xa lên đến 10 m - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" /></a></p>\r\n\r\n<h3>Thao t&aacute;c dễ d&agrave;ng nhờ thiết kế ph&iacute;m nhấn&nbsp;</h3>\r\n\r\n<p>Nhờ ph&iacute;m nhấn được t&iacute;ch hợp tr&ecirc;n th&acirc;n tai nghe gi&uacute;p bạn dễ d&agrave;ng&nbsp;<strong>bật trợ l&yacute; ảo, nghe/nhận cuộc gọi, ph&aacute;t/dừng chơi nhạc</strong>&nbsp;trở n&ecirc;n linh hoạt, nhanh ch&oacute;ng.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112612.jpg\" onclick=\"return false;\"><img alt=\"Hỗ trợ điều khiển dễ dàng bằng phím nhấn - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112612.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/256512/bluetooth-true-wireless-ava-ds200a-wb-031121-112612.jpg\" title=\"Hỗ trợ điều khiển dễ dàng bằng phím nhấn - Tai nghe Bluetooth True Wireless AVA+ DS200A-WB\" /></a></p>\r\n\r\n<p>Như vậy,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/bluetooth-true-wireless-ava-ds200a-wb\" target=\"_blank\" title=\"Xem thêm về Tai nghe Bluetooth True Wireless AVA+ DS200A-WB tại Thế Giới Di Động\">tai nghe Bluetooth True Wireless AVA+ DS200A-WB</a>&nbsp;ngo&agrave;i sở hữu cho m&igrave;nh nhiều đặc điểm nổi bật như thiết kế nhỏ gọn, chất &acirc;m cao, tối ưu trải nghiệm c&ugrave;ng chuẩn Bluetooth 5.0 ổn định, n&oacute; c&ograve;n sở hữu gi&aacute; cả v&ocirc; c&ugrave;ng phải chăng để ch&iacute;nh bạn c&oacute; thể trực tiếp trải nghiệm v&agrave; đ&aacute;nh gi&aacute;.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/bluetooth-true-wireless-ava-ds200a-wb-den-3.jpg', 'normal'),
(48, 16, 8, 'Bluetooth True Wireless AVA-DS206', 'TN005', 790000, 550000, 500, 'còn hàng', '<h3>Thiết kế sang chảnh, thời thượng, t&iacute;ch hợp c&ocirc;ng nghệ &acirc;m thanh Driver dynamic chất lượng, tương th&iacute;ch nhiều thiết bị v&agrave; điều khiển dễ d&agrave;ng,... l&agrave; những đặc điểm nổi bật nhất khi nhắc tới&nbsp;<a href=\"https://www.thegioididong.com/sp-291355\" target=\"_blank\" title=\"Tham khảo tai nghe Bluetooth True Wireless AVA+ DS206 đang bán trên Thế Giới Di Động\">tai nghe Bluetooth True Wireless AVA+ DS206</a>.</h3>\r\n\r\n<p>&bull; Thiết kế hộp sạc nhỏ gọn, vừa vặn khi nh&eacute;t t&uacute;i, bỏ balo v&agrave; đồng h&agrave;nh c&ugrave;ng bạn trong mọi chuyến h&agrave;nh tr&igrave;nh.</p>\r\n\r\n<p>&bull; Tai nghe dạng In-ear với phần n&uacute;m silicone mềm mại, &ecirc;m &aacute;i khi đeo l&acirc;u, đồng thời, thiết kế &ocirc;m s&aacute;t khu&ocirc;n tai gi&uacute;p&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo các sản phẩm tai nghe đang bán trên Thế Giới Di Động\">tai nghe</a>&nbsp;b&aacute;m chắc tr&ecirc;n tai trong c&aacute;c hoạt động thường ng&agrave;y.</p>\r\n\r\n<p>&bull; C&ocirc;ng nghệ &acirc;m thanh&nbsp;Driver dynamic tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-khong-day\" target=\"_blank\" title=\"Tham khảo các loại tai nghe True Wireless đang bán trên Thế Giới Di Động\">tai nghe True Wireless</a>&nbsp;n&agrave;y&nbsp;mang đến dải &acirc;m r&otilde; r&agrave;ng, chất lượng, đầy đủ &acirc;m bass, &acirc;m treble.</p>\r\n\r\n<p>&bull; Kết nối được với nhiều thiết bị qua Bluetooth 5.0 nhờ khả năng tương th&iacute;ch được với nhiều hệ điều h&agrave;nh phổ biến: Android, iOS, Windows, macOS.</p>\r\n\r\n<p>&bull; Hỗ trợ mic thoại tiện lợi với khả năng thu &acirc;m r&otilde; r&agrave;ng, liền mạch cho cuộc tr&ograve; chuyện giữa 2 b&ecirc;n thuận lợi.</p>\r\n\r\n<p>&bull; Điều khiển bằng cảm ứng chạm vuốt mượt m&agrave; tr&ecirc;n phần housing với c&aacute;c chức năng cơ bản như: Chuyển b&agrave;i h&aacute;t, ph&aacute;t/dừng nhạc, nhận/ngắt cuộc gọi v&agrave; bật trợ l&yacute; ảo.</p>\r\n\r\n<p>&bull; Dung lượng pin tr&ecirc;n chiếc&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-ava\" target=\"_blank\" title=\"Tham khảo tai nghe AVA+ đang bán trên Thế Giới Di Động\">tai nghe AVA+</a>&nbsp;n&agrave;y cho thời gian sử dụng khoảng 4 giờ v&agrave; mất 2 giờ để sạc đầy.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/tai-nghe-bluetooth-true-wireless-ava-ds206-hong-2.jpg', 'normal'),
(49, 16, 8, 'Tai nghe Có dây AVA+ DS815-WB', 'TN006', 200000, 160000, 200, 'còn hàng', '<h3><a href=\"https://www.thegioididong.com/sp-291356\" target=\"_blank\" title=\"Tai nghe Có dây AVA+ DS815-WB đang bán trên Thế Giới Di Động\">Tai nghe C&oacute; d&acirc;y AVA+ DS815-WB</a>&nbsp;mang đến một thiết kế đẹp mắt, sử dụng c&ocirc;ng nghệ &acirc;m thanh Driver dynamic, hỗ trợ mic thoại tiện dụng,... đ&aacute;p ứng đầy đủ những y&ecirc;u cầu cơ bản về một chiếc tai nghe.</h3>\r\n\r\n<p>&bull; Jack cắm Type-C chuy&ecirc;n dụng, kết nối được với nhiều thiết bị hiện nay, khi m&agrave; jack 3.5 mm đang dần được loại bỏ.</p>\r\n\r\n<p>&bull; Kiểu d&aacute;ng đơn giản, m&agrave;u sắc trang nh&atilde;, chiếc&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-ava\" target=\"_blank\" title=\"Tham khảo tai nghe AVA+ đang bán trên Thế Giới Di Động\">tai nghe AVA+</a>&nbsp;n&agrave;y chắc chắn sẽ l&agrave; m&oacute;n phụ kiện đẹp mắt ph&ugrave; hợp với mọi đối tượng người d&ugrave;ng.</p>\r\n\r\n<p>&bull; Khối lượng gọn nhẹ, phần d&acirc;y d&agrave;i 1.25 m đủ d&ugrave;ng m&agrave; vẫn dễ d&agrave;ng gấp gọn, cất giữ.</p>\r\n\r\n<p>&bull; &Acirc;m thanh ch&acirc;n thực, đầy đủ nhờ sử dụng c&ocirc;ng nghệ &acirc;m thanh Driver dynamic chất lượng gi&uacute;p bạn c&oacute; thể thoải m&aacute;i tận hưởng những bản nhạc y&ecirc;u th&iacute;ch mọi l&uacute;c mọi nơi.</p>\r\n\r\n<p>&bull; Ph&iacute;m điều khiển nằm ngay tr&ecirc;n d&acirc;y&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo các sản phẩm tai nghe đang bán trên Thế Giới Di Động\">tai nghe</a>&nbsp;cho ph&eacute;p bạn dễ d&agrave;ng điều chỉnh hợp l&yacute; mức &acirc;m lượng v&agrave; ph&aacute;t/dừng nhạc khi cần.</p>\r\n\r\n<p>&bull; Tương th&iacute;ch với nhiều thiết bị sử dụng hệ điều h&agrave;nh Android v&agrave; Windows</p>\r\n', 'admin', '01/12/2022', 'public/uploads/tai-nghe-co-day-type-c-ava-ds815-wb-trang-1-1.jpg', 'normal'),
(50, 16, 8, 'Tai nghe Bluetooth True Wireless AVA+ DS204A-WB', 'TN007', 600000, 375000, 500, 'còn hàng', '<h3>Đ&acirc;y gọn nhẹ,&nbsp;gam m&agrave;u trắng trẻ trung</h3>\r\n\r\n<p>Kiểu d&aacute;ng housing tinh tế, c&oacute; chu&ocirc;i cầm d&agrave;i cho ph&eacute;p bạn cầm nắm vững v&agrave;ng, đeo v&agrave;o tai cho độ b&aacute;m tốt. Về thiết kế hộp sạc th&igrave; vị tr&iacute; mở nắp hộp được l&agrave;m l&otilde;m cho bạn dễ d&agrave;ng đặt ng&oacute;n tay v&agrave;o v&agrave; bật mở nắp thuận tiện, kết cấu hộp sạc gọn g&agrave;ng, dễ d&agrave;ng sử dụng v&agrave; cất giữ trong t&uacute;i đeo ch&eacute;o, t&uacute;i x&aacute;ch lớn hoặc balo thường d&ugrave;ng của bạn.&nbsp;</p>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/tai-nghe-true-wireless-la-gi-uu-nhuoc-diem-the-nao-1201555\" target=\"_blank\" title=\"Tai nghe True Wireless là gì? Ưu nhược điểm thế nào? Có nên mua không?\">Tai nghe True Wireless l&agrave; g&igrave;? Ưu nhược điểm thế n&agrave;o? C&oacute; n&ecirc;n mua kh&ocirc;ng?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020753.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Vẻ ngoài sang trọng, đẹp mắt\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020753.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020753.jpg\" title=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Vẻ ngoài sang trọng, đẹp mắt\" /></a></p>\r\n\r\n<h3>Phủ đầy đ&ocirc;i tai bạn với chất &acirc;m sống động, cuốn h&uacute;t ngay từ nốt nhạc đầu ti&ecirc;n</h3>\r\n\r\n<p>Hơn nữa,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tham khảo tai nghe Bluetooth kinh doanh tại Thế Giới Di Động\">tai nghe Bluetooth</a>&nbsp;c&ograve;n được hỗ trợ&nbsp;<strong>micro</strong>&nbsp;đảm bảo qu&aacute; tr&igrave;nh tương t&aacute;c của bạn với đối t&aacute;c, người th&acirc;n thuận lợi, giọng thoại trong trẻo, sắc n&eacute;t.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020759.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Âm thanh được tái tạo chân thật, cực bắt tai\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020759.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020759.jpg\" title=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Âm thanh được tái tạo chân thật, cực bắt tai\" /></a></p>\r\n\r\n<h3>T&iacute;n hiệu truyền xa đến 10 m nhờ c&ocirc;ng nghệ kết nối kh&ocirc;ng d&acirc;y Bluetooth 5.0</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-ava\" target=\"_blank\" title=\"Tham khảo tai nghe AVA+ đang bán tại Thế Giới Di Động\">Tai nghe AVA+</a>&nbsp;l&agrave;m việc được với nhiều thiết bị kh&aacute;c nhau hoạt động tr&ecirc;n nền tảng&nbsp;Android, iOS, macOS v&agrave; Windows, duy tr&igrave; chất lượng kết nối mượt m&agrave; trong phạm vi rộng, hạn chế tối đa t&igrave;nh trạng ngắt kết nối, lag.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020805.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Chuẩn kết nối không dây Bluetooth 5.0 mượt mà\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020805.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020805.jpg\" title=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Chuẩn kết nối không dây Bluetooth 5.0 mượt mà\" /></a></p>\r\n\r\n<h3>Cung cấp thời gian sử dụng tai nghe liền mạch 5 giờ</h3>\r\n\r\n<p>Với thời lượng pin ấn tượng như vậy, bạn tha hồ gọi điện, nghe nhạc với&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe/bluetooth-true-wireless-ava-ds204a-wb-trang#top-article\" target=\"_blank\" title=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng\">tai nghe Bluetooth True Wireless AVA+ DS204A-WB trắng</a>, tận hưởng c&aacute;c sở th&iacute;ch vui chơi, l&agrave;m việc m&agrave; kh&ocirc;ng sợ hết pin. Nếu pin yếu? Bạn c&oacute; thể cắm sạc qua cổng Type-C v&agrave; chỉ mất&nbsp;<strong>2 giờ&nbsp;</strong>để lại c&oacute; tai nghe sử dụng tiếp.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-1-1.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Tổng thời gian trải nghiệm tai nghe lên đến 12 giờ\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-1-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-1-1.jpg\" title=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Tổng thời gian trải nghiệm tai nghe lên đến 12 giờ\" /></a></p>\r\n\r\n<h3>T&ugrave;y chỉnh c&aacute;c chức năng ch&iacute;nh x&aacute;c c&ugrave;ng cảm ứng chạm</h3>\r\n\r\n<p>Tr&ecirc;n housing c&oacute; t&iacute;ch hợp điều khiển cảm ứng cho ph&eacute;p bạn điều chỉnh&nbsp;ph&aacute;t/dừng chơi nhạc, chuyển b&agrave;i h&aacute;t, nghe/nhận cuộc gọi v&agrave; k&iacute;ch hoạt trợ l&yacute; giọng n&oacute;i dễ d&agrave;ng, nhanh ch&oacute;ng.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020817.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Điều khiển cảm ứng hiện đại, dễ dàng tùy chọn chuyển bài hát, phát/dừng nhạc, nghe/nhận cuộc gọi và bật trợ lý ảo\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020817.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/251825/bluetooth-true-wireless-ava-ds204a-wb-trang-031021-020817.jpg\" title=\"Tai nghe Bluetooth True Wireless AVA+ DS204A-WB Trắng - Điều khiển cảm ứng hiện đại, dễ dàng tùy chọn chuyển bài hát, phát/dừng nhạc, nghe/nhận cuộc gọi và bật trợ lý ảo\" /></a></p>\r\n\r\n<p>T&oacute;m lại,&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tai nghe giá rẻ chính hãng bán tại Thế Giới Di Động\">tai nghe</a>&nbsp;AVA+ DS204A-WB thật l&agrave; một phụ kiện nghe gọi tuyệt vời m&agrave; bạn n&ecirc;n c&acirc;n nhắc bởi n&oacute; kh&ocirc;ng chỉ sở hữu thiết kế gọn nhẹ m&agrave; c&ograve;n c&oacute; chất &acirc;m hay, thời gian d&ugrave;ng đến 17 giờ, chuẩn&nbsp;Bluetooth 5.0 cực mượt, dễ sử dụng v&agrave; mang theo b&ecirc;n m&igrave;nh.&nbsp;</p>\r\n', 'admin', '01/12/2022', 'public/uploads/bluetooth-true-wireless-ava-ds204a-wb-trang-1.jpeg', 'normal');
INSERT INTO `tbl_product` (`id`, `id_category`, `id_brand`, `name`, `code`, `price`, `promotional_price`, `quantity`, `status`, `description`, `user`, `create_date`, `image`, `level`) VALUES
(52, 16, 9, 'Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen', 'TN009', 1290000, 990000, 1000, 'còn hàng', '<h3>M&agrave;u đen đơn giản, thiết kế tinh xảo, lấy cảm hứng từ vi&ecirc;n đ&aacute; cuội</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe/tai-nghe-bluetooth-tws-realme-buds-q-rma215-den\" target=\"_blank\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 đen\">Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 đen</a>&nbsp;sở hữu đường cong G3 ho&agrave;n thiện, bề mặt được l&agrave;m nh&aacute;m từ c&ocirc;ng nghệ tia lửa 9 &micro;m cho cảm gi&aacute;c cầm cực k&igrave; dễ chịu, kh&ocirc;ng bị cấn tay.</p>\r\n\r\n<p>Trọng lượng 3.6 g/tai nghe nhẹ hơn cả trang giấy A4 (4.3 g), chất liệu polymer tổng hợp PC - ABS bền nhẹ, kết cấu&nbsp;coracoid, đeo v&agrave;o tai gọn g&agrave;ng, kh&ocirc;ng cảm gi&aacute;c vướng v&iacute;u. Hộp sạc thiết kế bo tr&ograve;n, gọn đẹp, đ&oacute;ng mở thuận tiện.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220720-110712.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Màu đen đơn giản, thiết kế tinh xảo\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220720-110712.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220720-110712.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Màu đen đơn giản, thiết kế tinh xảo\" /></a></p>\r\n\r\n<h3>Đệm tai &ecirc;m &aacute;i kết hợp với chất liệu mềm nhẹ cho bạn đeo thoải m&aacute;i suốt thời gian d&agrave;i</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110910.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Đệm tai êm ái\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110910.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110910.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Đệm tai êm ái\" /></a></p>\r\n\r\n<h3>Tận hưởng &acirc;m bass mạnh mẽ, cuốn h&uacute;t</h3>\r\n\r\n<p>Ứng dụng c&ocirc;ng nghệ&nbsp;Dynamic Bass Boost, trang bị driver bass 10 mm, m&agrave;ng chắn polymer tổng hợp PEEK &amp; PU cho &acirc;m thanh r&otilde; r&agrave;ng ở những tần số kh&aacute;c nhau, chất &acirc;m s&ocirc;i động, &acirc;m bass s&acirc;u hơn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110906.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Tận hưởng âm bass mạnh mẽ, cuốn hút\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110906.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110906.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Tận hưởng âm bass mạnh mẽ, cuốn hút\" /></a></p>\r\n\r\n<h3>Thời gian đ&agrave;m thoại 3 tiếng, nghe nhạc 4.5 tiếng, chơi game 5 tiếng v&agrave; 6 giờ xem phim li&ecirc;n tục</h3>\r\n\r\n<p>Mỗi&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo tai nghe kinh doanh tại Thế Giới Di Động\">tai nghe</a>&nbsp;c&oacute; dung lượng pin 40 mAh cộng th&ecirc;m dung lượng pin hộp sạc 400 mAh cung cấp đủ năng lượng cho bạn d&ugrave;ng trong tổng thời gian đến 20 tiếng. Sạc tiện lợi qua cổng Micro USB th&ocirc;ng dụng trong 2 tiếng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110842.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Tổng thời gian đến 20 tiếng\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110842.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110842.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Tổng thời gian đến 20 tiếng\" /></a></p>\r\n\r\n<h3>Kết nối kh&ocirc;ng d&acirc;y ngay lập tức trong khoảng c&aacute;ch đến 10 m với&nbsp;Bluetooth 5.0&nbsp;</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-realme\" target=\"_blank\" title=\"Tham khảo tai nghe Realme kinh doanh tại Thế Giới Di Động\">Tai nghe Realme</a>&nbsp;t&iacute;ch hợp con chip R1Q hỗ trợ kết nối Bluetooth nhanh ch&oacute;ng với c&aacute;c thiết bị&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại giá rẻ chính hãng bán tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo tablet giá rẻ chính hãng bán tại Thế Giới Di Động\">tablet</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tham khảo laptop giá rẻ chính hãng bán tại Thế Giới Di Động\">laptop</a>&nbsp;chạy hệ điều h&agrave;nh&nbsp;Android, Windows, iOS (iPhone).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110847.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Bluetooth 5.0\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110847.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110847.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Bluetooth 5.0\" /></a></p>\r\n\r\n<h3>Đồng bộ h&oacute;a &acirc;m thanh v&agrave; h&igrave;nh ảnh với độ trễ si&ecirc;u thấp 119 m/s&nbsp;</h3>\r\n\r\n<p>Con chip R1Q tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-khong-day\" target=\"_blank\" title=\"Tai nghe không dây giá rẻ chính hãng bán tại Thế Giới Di Động\">tai nghe True Wireless</a>&nbsp;sử dụng c&ocirc;ng nghệ truyền dẫn thời gian thực 2 k&ecirc;nh, cho khả năng kết nối 2 tai nghe độc lập với thiết bị. Khi chơi game cho độ trễ giảm chỉ c&ograve;n 119 m/s tạo n&ecirc;n sự đồng bộ giữa &acirc;m thanh v&agrave; h&igrave;nh ảnh gi&uacute;p bạn trải nghiệm giải tr&iacute; th&uacute; vị, hấp dẫn hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110853.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Độ trễ siêu thấp 119 ms\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110853.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110853.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Độ trễ siêu thấp 119 ms\" /></a></p>\r\n\r\n<h3>Dễ d&agrave;ng điều khiển qua ứng dụng&nbsp;realme Link</h3>\r\n\r\n<p>Th&ecirc;m&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-bluetooth\" target=\"_blank\" title=\"Tham khảo tai nghe Bluetooth kinh doanh tại Thế Giới Di Động\">tai nghe Bluetooth</a>&nbsp;v&agrave;o hệ sinh th&aacute;i&nbsp;AIoT của Realme, bạn tha hồ t&ugrave;y chỉnh c&aacute;c chức năng của tai nghe.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110901.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Realme Link\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110901.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220920-110901.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Realme Link\" /></a></p>\r\n\r\n<h3>Chống nước tối ưu với chuẩn&nbsp;IPX4</h3>\r\n\r\n<p>Ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m sử dụng, kh&ocirc;ng sợ nước mưa, mồ h&ocirc;i l&agrave;m hỏng thiết bị với chuẩn chống nước&nbsp;IPX4.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110838.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Chuẩn IPX4\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110838.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110838.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Chuẩn IPX4\" /></a></p>\r\n\r\n<h3>Điều khiển cảm ứng cho ph&eacute;p tuỳ chỉnh c&aacute;c chức năng</h3>\r\n\r\n<p>- Ph&aacute;t/tạm dừng b&agrave;i h&aacute;t, trả lời cuộc gọi: Chạm 2 lần v&agrave;o tai nghe.</p>\r\n\r\n<p>- Kết th&uacute;c cuộc gọi: Chạm v&agrave; giữ 1 b&ecirc;n tai nghe.</p>\r\n\r\n<p>- Chuyển b&agrave;i h&aacute;t tiếp theo: Chạm 3 lần v&agrave;o tai nghe.</p>\r\n\r\n<p>- V&agrave;o/tho&aacute;t chế độ tr&ograve; chơi: Chạm v&agrave; giữ cả 2 b&ecirc;n tai nghe.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110857.jpg\" onclick=\"return false;\"><img alt=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Điều khiển cảm ứng sử dụng các chức năng nhẹ nhàng\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110857.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/227355/tai-nghe-bluetooth-true-wireless-realme-buds-q-rma-220820-110857.jpg\" title=\"Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 Đen - Điều khiển cảm ứng sử dụng các chức năng nhẹ nhàng\" /></a></p>\r\n\r\n<p>Tai nghe Bluetooth True Wireless Realme Buds Q RMA215 đen chắc chắn l&agrave; một sự lựa chọn kh&aacute;c biệt v&agrave; đ&aacute;ng gi&aacute; m&agrave; bạn n&ecirc;n c&acirc;n nhắc với thời gian d&ugrave;ng &quot;khủng&quot;, thiết kế đẹp mắt, chất lượng tốt, dễ sử dụng,...</p>\r\n', 'admin', '01/12/2022', 'public/uploads/tai-nghe-bluetooth-tws-realme-buds-q-rma215-den-281220-0359170.jpg', 'normal'),
(53, 16, 9, 'True Wireless Realme Buds T10', 'TN008', 119000, 990000, 500, 'còn hàng', '<h3>Sở hữu thiết kế thời thượng, đẹp mắt, trang bị nhiều c&ocirc;ng nghệ hiện đại cả về &acirc;m thanh lẫn kết nối, chiếc&nbsp;<a href=\"https://www.thegioididong.com/sp-291354\" target=\"_blank\" title=\"Tai nghe Bluetooth True Wireless Realme Buds T100 đang bán trên Thế Giới Di Động\">tai nghe Bluetooth True Wireless Realme Buds T100</a>&nbsp;hứa hẹn sẽ c&ugrave;ng bạn tạo n&ecirc;n những trải nghiệm tuyệt vời trong cuộc sống.</h3>\r\n\r\n<p>&bull; Thiết kế hộp sạc hiện đại, đẹp mắt với gam m&agrave;u thời thượng, kiểu d&aacute;ng tối giản. Phần bản lề được chế t&aacute;c chỉn chu, chắc chắn cho thao t&aacute;c đ&oacute;ng mở hộp đựng dễ d&agrave;ng.</p>\r\n\r\n<p>&bull; Tai nghe gọn nhẹ cho cảm gi&aacute;c đeo nhẹ nh&agrave;ng, thoải m&aacute;i. Kết hợp c&ugrave;ng đệm tai silicone &ocirc;m kh&iacute;t khổ tai người d&ugrave;ng để bạn c&oacute; thể sử dụng trong mọi điều kiện hoạt động.&nbsp;</p>\r\n\r\n<p>&bull; Trang bị c&aacute;c c&ocirc;ng nghệ hiện đại về &acirc;m thanh như: C&ocirc;ng nghệ ENC chống ồn v&agrave; Dynamic Bass cho chất &acirc;m trong trẻo, r&otilde; r&agrave;ng, &acirc;m bass đầy đủ, tối ưu trải nghiệm nghe nhạc.</p>\r\n\r\n<p>&bull; T&iacute;ch hợp mic thoại tiện dụng ngay tr&ecirc;n tai với khả năng thu &acirc;m ch&iacute;nh x&aacute;c, đầy đủ th&ocirc;ng tin gi&uacute;p đối phương kh&ocirc;ng bị bỏ lỡ chi tiết quan trọng n&agrave;o của cuộc tr&ograve; chuyện.</p>\r\n\r\n<p>&bull; C&ocirc;ng nghệ Bluetooth chuẩn 5.3 ti&ecirc;n tiến gi&uacute;p kết nối nhanh ch&oacute;ng, ổn định.</p>\r\n\r\n<p>&bull; Hỗ trợ chống nước IPX5 cho ph&eacute;p bạn sử dụng trong c&aacute;c hoạt động thể thao h&agrave;ng ng&agrave;y m&agrave; kh&ocirc;ng lo mồ h&ocirc;i sẽ l&agrave;m ảnh hưởng đến chất lượng hoạt động của chiếc&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe-realme\" target=\"_blank\" title=\"Tham khảo tai nghe Realme đang bán tại Thế Giới Di Động\">tai nghe Realme</a>&nbsp;n&agrave;y.</p>\r\n\r\n<p>&bull; Điều khiển trực tiếp tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tham khảo các sản phẩm tai nghe đang bán trên Thế Giới Di Động\">tai nghe</a>&nbsp;th&ocirc;ng qua phần cảm ứng được t&iacute;ch hợp sẵn. Ngo&agrave;i ra, bạn c&oacute; thể tải ứng dụng Realme Link để theo d&otilde;i v&agrave; điều khiển được nhiều chức năng của tai nghe hơn.</p>\r\n\r\n<p>&bull; Dung lượng pin lớn với khả năng sử dụng li&ecirc;n tục đến 6 giờ v&agrave; k&eacute;o d&agrave;i tới 28 giờ khi d&ugrave;ng chung với hộp sạc.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/tai-nghe-bluetooth-tws-realme-buds-t100-den-1.jpg', 'normal'),
(54, 16, 7, 'Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen', 'TN010', 610000, 490000, 500, 'còn hàng', '<p>Tai nghe được thiết kế d&agrave;nh ri&ecirc;ng cho c&aacute;c t&iacute;n đồ game với&nbsp;<strong>kiểu d&aacute;ng hiện đại</strong>, m&agrave;u đen c&aacute; t&iacute;nh.</p>\r\n\r\n<p>Đặc biệt logo thương hiệu được in tr&ecirc;n mặt tai nghe k&egrave;m đ&egrave;n nền LED cho tai nghe v&ocirc; c&ugrave;ng nổi bật, đậm chất gaming.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den3-1.jpg\" onclick=\"return false;\"><img alt=\"Thiết kế cá tính, đậm chất gaming - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den3-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den3-1.jpg\" title=\"Thiết kế cá tính, đậm chất gaming - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" /></a></p>\r\n\r\n<p><strong>Microphone&nbsp;c&oacute; thiết kế nhỏ gọn</strong>, khả năng lọc tạp &acirc;m tối ưu, dễ d&agrave;ng điều chỉnh mic cho ph&ugrave; hợp với từng người d&ugrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den2-1.jpg\" onclick=\"return false;\"><img alt=\"Microphone dạng lò xo, dễ dàng điều chỉnh - Quai đeo và chụp tai có đệm êm, thoải mái khi sử dụng - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den2-1.jpg\" title=\"Microphone dạng lò xo, dễ dàng điều chỉnh - Quai đeo và chụp tai có đệm êm, thoải mái khi sử dụng - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-mozard\" target=\"_blank\" title=\"Tai nghe Mozard giá rẻ chính hãng bán tại Thế Giới Di Động\">Tai nghe Mozard</a>&nbsp;sử dụng&nbsp;<strong>c&ocirc;ng nghệ &acirc;m thanh v&ograve;m 7.1&nbsp;</strong>c&oacute;&nbsp;m&ocirc;i trường&nbsp;<strong>&acirc;m thanh 3D</strong>&nbsp;với độ trung thực cao gi&uacute;p bạn c&oacute; thể nghe r&otilde; cả tiếng s&uacute;ng, tiếng bước ch&acirc;n trong game. Nghe nhạc hay khi bật &acirc;m lượng lớn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den4.jpg\" onclick=\"return false;\"><img alt=\"Công nghệ âm thanh vòm 7.1 chân thực - Microphone dạng lò xo, dễ dàng điều chỉnh - Quai đeo và chụp tai có đệm êm, thoải mái khi sử dụng - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den4.jpg\" title=\"Công nghệ âm thanh vòm 7.1 chân thực - Microphone dạng lò xo, dễ dàng điều chỉnh - Quai đeo và chụp tai có đệm êm, thoải mái khi sử dụng - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe\" target=\"_blank\" title=\"Tai nghe giá rẻ chính hãng bán tại Thế Giới Di Động\">Tai nghe</a>&nbsp;sử dụng&nbsp;<strong>Jack cắm USB</strong>&nbsp;th&ocirc;ng dụng nhất hiện nay, ph&ugrave; hợp cho hầu hết c&aacute;c loại&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop giá rẻ chính hãng bán tại Thế Giới Di Động\">laptop</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-de-ban\" target=\"_blank\" title=\"Máy tính để bàn giá rẻ chính hãng bán tại Thế Giới Di Động\">m&aacute;y t&iacute;nh để b&agrave;n</a>, c&oacute;&nbsp;<strong>chiều d&agrave;i d&acirc;y l&ecirc;n đến 205 cm</strong>&nbsp;kết nối dễ d&agrave;ng ở khoảng c&aacute;ch xa.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den-3.png\" onclick=\"return false;\"><img alt=\"Sử dụng jack cắm thông dụng nhất hiện nay - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den-3.png\" src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den-3.png\" title=\"Sử dụng jack cắm thông dụng nhất hiện nay - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/tai-nghe-gaming\" target=\"_blank\" title=\"Tham khảo tai nghe gaming tại Thế Giới Di Động\">Tai nghe chơi game</a><strong>&nbsp;với m&agrave;ng loa &acirc;m thanh 40 mm</strong>&nbsp;cho &acirc;m thanh th&ecirc;m mạnh mẽ sống động, đặc biệt c&oacute; lớp đệm &acirc;m &aacute;i tr&ecirc;n quai&nbsp;đeo v&agrave; tai chụp kh&ocirc;ng g&acirc;y &aacute;p lực trực tiếp l&ecirc;n c&aacute;c m&ocirc; mềm của tai.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den3.jpg\" onclick=\"return false;\"><img alt=\"Quai đeo và tai chụp có đệm êm, thoải mái khi sử dụng - Quai đeo và chụp tai có đệm êm, thoải mái khi sử dụng - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den3.jpg\" title=\"Quai đeo và tai chụp có đệm êm, thoải mái khi sử dụng - Quai đeo và chụp tai có đệm êm, thoải mái khi sử dụng - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" /></a></p>\r\n\r\n<p>N&uacute;t tăng giảm &acirc;m lượng được thiết kế một b&ecirc;n chụp tai thuận lợi cho việc điều chỉnh &acirc;m thanh ph&ugrave; hợp với c&aacute;c loại tr&ograve; chơi.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den-5.png\" onclick=\"return false;\"><img alt=\"Tăng giảm âm lượng thuận tiện với bánh lăn một bên chụp tai - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" data-src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den-5.png\" src=\"https://cdn.tgdd.vn/Products/Images/54/214560/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den-5.png\" title=\"Tăng giảm âm lượng thuận tiện với bánh lăn một bên chụp tai - Tai nghe chụp tai Gaming MozardX DS902 7.1 Đen\" /></a></p>\r\n\r\n<p>Nh&igrave;n chung, với kiểu d&aacute;ng bắt mắt c&ugrave;ng chất lượng &acirc;m thanh tuyệt vời, sử dụng chuẩn c&ocirc;ng nghệ &acirc;m thanh v&ograve;m 7.1, tai nghe chụp tai Gaming MozardX DS904 7.1 đen ch&iacute;nh l&agrave; một gợi &yacute; ho&agrave;n hảo cho c&aacute;c game thủ.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/tai-nghe-chup-tai-gaming-mozardx-ds902-71-den-2-org.jpg', 'normal'),
(55, 17, 7, 'Loa Bluetooth Mozard BT100 Đen', 'Loa001', 1790000, 1190000, 1000, 'còn hàng', '<h3>Thiết kế chắc chắn, m&agrave;u đen thanh lịch</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/loa-laptop/bluetooth-mozard-bt100\" target=\"_blank\" title=\"Loa Bluetooth Mozard BT100\">Loa Bluetooth Mozard BT100</a>&nbsp;c&oacute; kiểu d&aacute;ng đơn giản, c&aacute;c g&oacute;c bo cong &ocirc;m trọn th&acirc;n loa cho thiết kế ho&agrave;n thiện, sử dụng chất liệu vải để lọc &acirc;m v&agrave; được bọc lớp da xung quanh tr&aacute;nh việc b&aacute;m v&acirc;n tay.&nbsp;<strong>Dạng&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-bluetooth\" target=\"_blank\" title=\"Loa Bluetooth bán tại Thế Giới Di Động\">loa Bluetooth</a>&nbsp;nhỏ nhắn</strong>, dễ d&agrave;ng mang theo để nghe nhạc cho c&aacute;c chuyến đi xa th&ecirc;m th&uacute; vị, phấn kh&iacute;ch.&nbsp;</p>\r\n\r\n<p>Thiết bị&nbsp;<strong>d&ugrave;ng điện trực tiếp</strong>, kh&ocirc;ng cần quan t&acirc;m đến vấn đề hết pin, chỉ cần nơi n&agrave;o c&oacute; nguồn điện, bạn c&oacute; thể kết nối v&agrave; sử dụng ngay.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-1-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard BT100 Đen - Thiết kế chắc chắn, màu đen thanh lịch\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-1-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-1-1.jpg\" title=\"Loa Bluetooth Mozard BT100 Đen - Thiết kế chắc chắn, màu đen thanh lịch\" /></a></p>\r\n\r\n<h3>T&aacute;i tạo &acirc;m thanh sống động với tổng c&ocirc;ng suất 45 W</h3>\r\n\r\n<p>Mức c&ocirc;ng suất lớn cho ph&eacute;p&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-mozard\" target=\"_blank\" title=\"Loa Mozard đang bán tại Thế Giới Di Động\">loa Mozard</a>&nbsp;tạo n&ecirc;n&nbsp;<strong>chất &acirc;m mạnh mẽ</strong>, c&aacute;c dải &acirc;m kh&aacute; t&aacute;ch bạch,&nbsp;<strong>thưởng thức được nhiều thể loại nhạc</strong>&nbsp;từ bolero đến jazz, pop.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100--2-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard BT100 Đen - Âm thanh rõ ràng, cuốn hút qua tổng công suất loa 45W\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100--2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100--2-1.jpg\" title=\"Loa Bluetooth Mozard BT100 Đen - Âm thanh rõ ràng, cuốn hút qua tổng công suất loa 45W\" /></a></p>\r\n\r\n<h3>Khoảng c&aacute;ch kết nối xa đến<strong>&nbsp;10 m&nbsp;</strong>nhờ c&ocirc;ng nghệ<strong>&nbsp;Bluetooth</strong></h3>\r\n\r\n<p>Sản phẩm tương th&iacute;ch với điện thoại, laptop chạy tr&ecirc;n<strong>&nbsp;hệ điều h&agrave;nh Android, Windows, iOS (iPhone),</strong>&nbsp;đảm bảo đường truyền mượt, hạn chế giật, lag.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-3.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard BT100 Đen - Khoảng cách kết nối xa đến 10 m nhờ công nghệ Bluetooth\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-3.jpg\" title=\"Loa Bluetooth Mozard BT100 Đen - Khoảng cách kết nối xa đến 10 m nhờ công nghệ Bluetooth\" /></a></p>\r\n\r\n<h3>Điều khiển trực quan dễ thao t&aacute;c</h3>\r\n\r\n<p>Mặt trước của&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Loa đang bán tại Thể Giới Di Động\">loa</a>&nbsp;c&oacute; c&aacute;c c&ocirc;ng tắc điều chỉnh tăng/giảm &acirc;m lượng, chuyển b&agrave;i h&aacute;t, ph&aacute;t/dừng chơi nhạc đơn giản, nhanh nhẹn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-4.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard BT100 Đen - Điều khiển trực quan dễ thao tác\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100-4.jpg\" title=\"Loa Bluetooth Mozard BT100 Đen - Điều khiển trực quan dễ thao tác\" /></a></p>\r\n\r\n<h3>Kết nối đa nền tảng nhờ t&iacute;ch hợp nhiều dạng kết nối th&ocirc;ng dụng</h3>\r\n\r\n<p>Bao gồm&nbsp;<strong>Bluetooth, USB, jack 3.5 mm ra RCA</strong>, hỗ trợ bạn phối gh&eacute;p với c&aacute;c thiết bị dễ d&agrave;ng, đ&aacute;p ứng nhu cầu trải nghiệm phong ph&uacute; của bạn mỗi ng&agrave;y.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100--5-fix.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard BT100 Đen - Kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100--5-fix.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/236680/bluetooth-mozard-bt100--5-fix.jpg\" title=\"Loa Bluetooth Mozard BT100 Đen - Kết nối\" /></a></p>\r\n\r\n<p>&quot;Nhỏ m&agrave; c&oacute; v&otilde;&quot; - Mozard BT100 mẫu loa nhỏ xinh, c&ocirc;ng suất lớn, kết nối tiện &iacute;ch, đ&acirc;y sẽ l&agrave; sản phẩm loa thực sự sẽ l&agrave;m bạn h&agrave;i l&ograve;ng khi sử dụng.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/vi-vn-bluetooth-mozard-bt100-thumbvideo.jpg', 'discount'),
(56, 17, 7, 'Loa Bluetooth Mozard S21', 'Loa002', 550000, 350000, 500, 'còn hàng', '<h3>Thiết kế nhỏ gọn, dễ mang đi, c&oacute; 3 gam m&agrave;u trẻ trung tuỳ chọn</h3>\r\n\r\n<p>Loa Bluetooth Mozard S21 c&oacute; 3 m&agrave;u gồm&nbsp;<strong>xanh dương, đỏ v&agrave; x&aacute;m đen</strong>&nbsp;dễ lựa chọn. K&iacute;ch thước&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Tham khảo loa laptop kinh doanh tại Thế Giới Di Động\">loa</a>&nbsp;nhỏ gọn, kiểu d&aacute;ng hiện đại, tinh tế, dễ d&agrave;ng để mang theo sử dụng cho mọi kh&ocirc;ng gian.</p>\r\n\r\n<p>M&agrave;ng loa căng mịn chắc chắn, lớp vỏ bọc cao su giữ loa chống trầy v&agrave; chống trượt khi đặt tr&ecirc;n c&aacute;c mặt phẳng trơn b&oacute;ng. Với trọng lượng chỉ&nbsp;<strong>0.277&nbsp;g,</strong>&nbsp;thật nhẹ nh&agrave;ng để bảo quản v&agrave; mang theo sử dụng loa mọi l&uacute;c, mọi nơi.</p>\r\n\r\n<p>Đi k&egrave;m c&ograve;n c&oacute;&nbsp;<strong>quai m&oacute;c xỏ ng&oacute;n</strong>, cho ph&eacute;p bạn treo m&oacute;c loa ở vị tr&iacute; ph&ugrave; hợp.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-11.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard S21 - Thiết kế\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-11.jpg\" title=\"Loa Bluetooth Mozard S21 - Thiết kế\" /></a></p>\r\n\r\n<h3>Kết nối ổn định qua Bluetooth 5.0 trong phạm vi 10 m</h3>\r\n\r\n<p><strong>Kết nối kh&ocirc;ng d&acirc;y chất lượng cao&nbsp;</strong>với&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\">m&aacute;y t&iacute;nh bảng</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop-ldp\" target=\"_blank\">laptop</a>&nbsp;cho đường truyền ổn định, khoảng c&aacute;ch kết nối đến&nbsp;<strong>10 m</strong>&nbsp;để bạn thỏa sức s&aacute;ng tạo kh&ocirc;ng gian giải tr&iacute; &acirc;m nhạc của c&aacute; nh&acirc;n hay c&ugrave;ng b&egrave; bạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-2-1.jpg\" onclick=\"return false;\"><img alt=\"Bluetooth 5.0 - Loa Bluetooth Mozard S21\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-2-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-2-1.jpg\" title=\"Bluetooth 5.0 - Loa Bluetooth Mozard S21\" /></a></p>\r\n\r\n<h3>&Acirc;m thanh sống động, &acirc;m bass s&acirc;u cuốn h&uacute;t với c&ocirc;ng suất 5W</h3>\r\n\r\n<p>Để bạn trải nghiệm mọi thể loại &acirc;m nhạc m&agrave; m&igrave;nh y&ecirc;u th&iacute;ch một c&aacute;ch thật tuyệt vời tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-mozard\" target=\"_blank\" title=\"Tham khảo các sản phẩm loa Mozard kinh doanh tại Điện máy XANH\">loa Bluetooth Mozard</a>.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-3-1.jpg\" onclick=\"return false;\"><img alt=\"Âm thanh sống động - Loa Bluetooth Mozard S21\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-3-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-3-1.jpg\" title=\"Âm thanh sống động - Loa Bluetooth Mozard S21\" /></a></p>\r\n\r\n<h3>An to&agrave;n để d&ugrave;ng ngo&agrave;i trời nhờ chuẩn kh&aacute;ng bụi v&agrave; chống nước IPX6</h3>\r\n\r\n<p>Thoải m&aacute;i sử dụng Mozard S21 ở bờ biển, cạnh bể bơi hay trong c&aacute;c bữa party ngo&agrave;i trời m&agrave; kh&ocirc;ng lo nhiễm bụi, nhiễm nước g&acirc;y hư hại nhờ&nbsp;<strong>chuẩn IPX6</strong>&nbsp;được trang bị tr&ecirc;n loa, tự do b&ugrave;ng ch&aacute;y c&ugrave;ng bữa tiệc giải tr&iacute; của bạn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-4-1.jpg\" onclick=\"return false;\"><img alt=\"IPX6 - Loa Bluetooth Mozard S21\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-4-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-4-1.jpg\" title=\"IPX6 - Loa Bluetooth Mozard S21\" /></a></p>\r\n\r\n<h3>Sử dụng pin sạc tiện lợi</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/loa-laptop-bluetooth\" target=\"_blank\" title=\"Loa Bluetooth chính hãng giá rẻ bán tại Thế Giới Di Động\">Loa bluetooth</a>&nbsp;sử dụng pin sạc<strong>&nbsp;800 mAh</strong>, sạc đầy trong&nbsp;<strong>2 giờ</strong>, d&ugrave;ng li&ecirc;n tục đến<strong>&nbsp;6 &ndash; 7 giờ</strong>&nbsp;(&acirc;m lượng trung b&igrave;nh).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-5.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc - Loa Bluetooth Mozard S21\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-5.jpg\" title=\"Pin sạc - Loa Bluetooth Mozard S21\" /></a></p>\r\n\r\n<h3>Điều khiển đơn giản qua c&aacute;c n&uacute;t bấm dễ nhận biết chức năng</h3>\r\n\r\n<p>- N&uacute;t nguồn.</p>\r\n\r\n<p>- Tăng/giảm &acirc;m lượng.</p>\r\n\r\n<p>- Dừng/ph&aacute;t nhạc.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-6.jpg\" onclick=\"return false;\"><img alt=\"Dễ dùng - Loa Bluetooth Mozard S21\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247505/bluetooth-mozard-s21-6.jpg\" title=\"Dễ dùng - Loa Bluetooth Mozard S21\" /></a></p>\r\n\r\n<p>Với loa Bluetooth Mozard S21 , tin tưởng bạn sẽ c&oacute; những bữa tiệc đầy s&ocirc;i động, hứng khởi v&agrave; thỏa m&atilde;n c&ugrave;ng bản nhạc y&ecirc;u th&iacute;ch v&agrave; chất lượng &acirc;m thanh ấn tượng từ chiếc loa mini tinh tế.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/bluetooth-mozard-s21-do-1.jpeg', 'normal'),
(57, 16, 7, 'Loa Bluetooth Mozard Y550 Plus', 'Loa003', 550000, 500000, 1000, 'hàng sắp về', '<h3><a href=\"https://www.thegioididong.com/sp-297267\" target=\"_blank\" title=\"Loa Bluetooth Mozard Y550 Plus đang bán tại Thế Giới Di Động\">Loa Bluetooth Mozard Y550 Plus</a>&nbsp;c&oacute; thiết kế nhỏ gọn, kiểu d&aacute;ng thời thượng, tiện lợi cất giữ, b&ugrave;ng nổ kh&ocirc;ng gian &acirc;m nhạc của ri&ecirc;ng bạn.</h3>\r\n\r\n<p>&bull;&nbsp;Thiết kế lăng trụ tam gi&aacute;c với c&aacute;c cạnh, viền được bo mềm mại, đồng thời, loa trang bị ch&acirc;n đế cao su gi&uacute;p b&aacute;m chắc chắn tr&ecirc;n c&aacute;c mặt phẳng.</p>\r\n\r\n<p>&bull;&nbsp;Khối lượng gọn nhẹ, k&iacute;ch thước đủ để cầm trong l&ograve;ng b&agrave;n tay, cất giữ v&agrave; mang theo thoải m&aacute;i.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Tham khảo loa đang bán tại Thế Giới Di Động\">Loa</a>&nbsp;mang đến &acirc;m thanh sống động, to r&otilde;, đ&aacute;p ứng tốt c&aacute;c nhu cầu c&aacute; nh&acirc;n của bạn, khi cần mở rộng kh&ocirc;ng gian nghe nhạc, bạn c&oacute; thể kết nối c&ugrave;ng với 1 loa kh&aacute;c.</p>\r\n\r\n<p>&bull;&nbsp;Ph&aacute;t nhạc bằng c&aacute;ch kết nối kh&ocirc;ng d&acirc;y với thiết bị th&ocirc;ng qua Bluetooth hoặc c&aacute;c t&ugrave;y chọn kh&aacute;c như: Jack 3.5 mm v&agrave; thẻ nhớ Micro SD.</p>\r\n\r\n<p>&bull;&nbsp;Bảo vệ&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-mozard\" target=\"_blank\" title=\"Tham khảo loa Mozard đang bán tại Thế Giới Di Động\">loa Mozard</a>&nbsp;an to&agrave;n khi v&ocirc; t&igrave;nh bị d&iacute;nh nước với chuẩn chống nước IPX6.</p>\r\n\r\n<p>&bull;&nbsp;Dung lượng pin cao cho thời lượng sử dụng c&oacute; thể l&ecirc;n 4 - 8 tiếng (t&ugrave;y v&agrave;o mức d&ugrave;ng của bạn)</p>\r\n', 'admin', '01/12/2022', 'public/uploads/loa-bluetooth-mozard-y550-plus-xanh-1.jpeg', 'normal'),
(58, 16, 7, 'Loa Bluetooth Mozard X21', 'Loa004', 550000, 385000, 500, 'hết hàng', '<h3>Thiết kế gọn đẹp, bề mặt phủ sắc đen thời thượng, đ&iacute;nh k&egrave;m d&acirc;y treo tiện di chuyển</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/loa-laptop/bluetooth-mozard-x21\" target=\"_blank\" title=\"Loa Bluetooth Mozard X21\">Loa Bluetooth Mozard X21</a>&nbsp;c&oacute; dạng h&igrave;nh tr&ograve;n, chi tiết tinh tế cho cảm gi&aacute;c sang trọng, cao cấp. T&iacute;ch hợp d&acirc;y treo đồng m&agrave;u cho ph&eacute;p bạn treo m&oacute;c, mang theo tiện lợi khi đi du lịch, c&ocirc;ng t&aacute;c hoặc đơn giản l&agrave; di chuyển giữa c&aacute;c ph&ograve;ng trong nh&agrave;.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard X21 - Thiết kế gọn đẹp, bề mặt phủ sắc đen thời thượng, đính kèm dây treo tiện di chuyển\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-1.jpg\" title=\"Loa Bluetooth Mozard X21 - Thiết kế gọn đẹp, bề mặt phủ sắc đen thời thượng, đính kèm dây treo tiện di chuyển\" /></a></p>\r\n\r\n<h3>&Acirc;m thanh sinh động, &acirc;m trầm chắc khỏe với c&ocirc;ng suất 5 W</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-2.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard X21 - Âm thanh sinh động, âm trầm chắc khỏe với công suất 5 W\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-2.jpg\" title=\"Loa Bluetooth Mozard X21 - Âm thanh sinh động, âm trầm chắc khỏe với công suất 5 W\" /></a></p>\r\n\r\n<h3>Chuẩn kết nối kh&ocirc;ng d&acirc;y Bluetooth 5.0 cho khoảng c&aacute;ch sử dụng l&ecirc;n tới 10 m</h3>\r\n\r\n<p>Kết hợp d&ugrave;ng với nhiều thiết bị kh&aacute;c nhau như&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop giá rẻ chính hãng bán tại Thế Giới Di Động\">laptop</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>,...,&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Loa đang bán tại Thể Giới Di Động\">loa</a>&nbsp;duy tr&igrave; t&iacute;n hiệu chất lượng để bạn nghe nhạc, xem phim kh&ocirc;ng bị gi&aacute;n đoạn.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-3.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard X21 - Chuẩn kết nối không dây Bluetooth 5.0 cho khoảng cách sử dụng lên tới 10 m\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-3.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-3.jpg\" title=\"Loa Bluetooth Mozard X21 - Chuẩn kết nối không dây Bluetooth 5.0 cho khoảng cách sử dụng lên tới 10 m\" /></a></p>\r\n\r\n<h3>Thoải m&aacute;i nghe nhạc cả khi đang tắm nhờ ti&ecirc;u chuẩn&nbsp;chống nước IPX6</h3>\r\n\r\n<p>&nbsp;IPX6 cho&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-mozard\" target=\"_blank\" title=\"Loa Mozard đang bán tại Thế Giới Di Động\">loa Mozard</a>&nbsp;c&oacute; khả năng chịu được&nbsp;tia nước mạnh trong tối thiểu 3 ph&uacute;t n&ecirc;n bạn c&oacute; thể d&ugrave;ng trong ph&ograve;ng tắm hoặc mang theo b&ecirc;n m&igrave;nh khi đi cắm trại ngo&agrave;i trời, đi chơi biển,...&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-4.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard X21 - Thoải mái nghe nhạc cả khi đang tắm nhờ tiêu chuẩn chống nước IPX6\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-4.jpg\" title=\"Loa Bluetooth Mozard X21 - Thoải mái nghe nhạc cả khi đang tắm nhờ tiêu chuẩn chống nước IPX6\" /></a></p>\r\n\r\n<h3>Sử dụng liền mạch đến 8 - 10 tiếng (&acirc;m lượng trung b&igrave;nh) với&nbsp;dung lượng pin 1500 mAh</h3>\r\n\r\n<p>Cho bạn nghe trọn vẹn cả danh s&aacute;ch nhạc y&ecirc;u th&iacute;ch m&agrave; kh&ocirc;ng cần lo lắng loa bị hết pin giữa chừng v&agrave; khi&nbsp;pin yếu, chỉ mất tầm&nbsp;<strong>2 tiếng&nbsp;</strong>sạc v&agrave; thiết bị lại đủ năng lượng cho bạn tiếp tục trải nghiệm.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-6.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard X21 - Sử dụng liền mạch đến 8 - 10 tiếng (âm lượng trung bình) với dung lượng pin 1500 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-6.jpg\" title=\"Loa Bluetooth Mozard X21 - Sử dụng liền mạch đến 8 - 10 tiếng (âm lượng trung bình) với dung lượng pin 1500 mAh\" /></a></p>\r\n\r\n<h3>T&iacute;ch hợp điều khiển n&uacute;t nhấn c&oacute; k&yacute; hiệu r&otilde; r&agrave;ng dễ bật/tắt nguồn, tăng/giảm &acirc;m lượng</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-5.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Mozard X21 - Tích hợp điều khiển nút nhấn có ký hiệu rõ ràng dễ bật/tắt nguồn, tăng/giảm âm lượng loa \" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/247516/bluetooth-mozard-x21-77-5.jpg\" title=\"Loa Bluetooth Mozard X21 - Tích hợp điều khiển nút nhấn có ký hiệu rõ ràng dễ bật/tắt nguồn, tăng/giảm âm lượng loa \" /></a></p>\r\n\r\n<p>Mozard X21 l&agrave; mẫu&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-bluetooth\" target=\"_blank\" title=\"Loa Bluetooth chính hãng giá rẻ bán tại Thế Giới Di Động\">loa Bluetooth</a>&nbsp;nhỏ gọn, xinh xắn, với chất &acirc;m trung thực từ c&ocirc;ng suất 5 W, thời lượng pin tối đa 10 tiếng, chuẩn chống&nbsp;IPX6, dễ d&agrave;ng điều khiển, di chuyển, xứng đ&aacute;ng đồng h&agrave;nh c&ugrave;ng bạn tr&ecirc;n mọi chuyến đi.&nbsp;</p>\r\n', 'admin', '01/12/2022', 'public/uploads/x21-thumb-1020x570-1.jpg', 'normal');
INSERT INTO `tbl_product` (`id`, `id_category`, `id_brand`, `name`, `code`, `price`, `promotional_price`, `quantity`, `status`, `description`, `user`, `create_date`, `image`, `level`) VALUES
(59, 17, 10, 'Loa Bluetooth Sony Extra Bass SRS-XB33', 'Loa005', 3690000, 3550000, 200, 'còn hàng', '<h3>Loa kiểu d&aacute;ng năng động, nhiều m&agrave;u sắc lựa chọn</h3>\r\n\r\n<p>Thiết kế cầm vừa tay, c&oacute; thể trực tiếp cầm loa nghe nhạc hoặc cho v&agrave;o t&uacute;i x&aacute;ch, balo khi đi chơi, du lịch, đi l&agrave;m,... Với 3 gam m&agrave;u nổi bật l&agrave; đen, đỏ v&agrave; xanh dương, bạn nam v&agrave; nữ c&oacute; thể tiện chọn mua theo sở th&iacute;ch của m&igrave;nh.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083608.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Kiểu dáng năng động\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083608.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083608.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Kiểu dáng năng động\" /></a></p>\r\n\r\n<p>Gắn ch&acirc;n đế cứng chắc, đặt sản phẩm nằm ngang tr&ecirc;n mọi bề mặt thăng bằng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123420-083457.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Gắn chân đế cứng chắc\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123420-083457.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123420-083457.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Gắn chân đế cứng chắc\" /></a></p>\r\n\r\n<h3>&Acirc;m thanh cuốn h&uacute;t, s&ocirc;i động qua c&ocirc;ng nghệ&nbsp;Extra Bass độc quyền của Sony</h3>\r\n\r\n<p>Nhờ cặp loa X- Balanced thiết kế m&agrave;ng loa h&igrave;nh oval được tối đa tiết diện gi&uacute;p tăng &aacute;p suất buồng &acirc;m, &acirc;m lượng, giảm m&eacute;o tiếng cho &acirc;m thanh sống động, &acirc;m bass mạnh mẽ, trầm s&acirc;u.</p>\r\n\r\n<p>Bộ tản nhiệt thụ động được xử l&yacute; tối ưu cho &acirc;m bass lực hơn. N&acirc;ng cao chất lượng &acirc;m thanh với n&oacute;n loa&nbsp;l&agrave;m từ vật liệu cellular gia cường bằng mica (MRC).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-135420-085455.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Extra Bass\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-135420-085455.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-135420-085455.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Extra Bass\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083509.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Màng loa\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083509.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083509.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Màng loa\" /></a></p>\r\n\r\n<p><a href=\"https://www.thegioididong.com/loa-laptop-sony-bluetooth\" target=\"_blank\" title=\"Loa Bluetooth Sony giá rẻ chính hãng bán tại Thế Giới Di Động\">Loa Bluetooth Sony</a>&nbsp;mang lại cảm gi&aacute;c tiệc t&ugrave;ng mọi l&uacute;c nhờ &acirc;m thanh 3 chiều ch&acirc;n thật, phủ khắp gi&aacute;c quan bạn từ t&iacute;nh năng Live Sound.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083524.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Live Sound\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083524.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083524.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Live Sound\" /></a></p>\r\n\r\n<h3>Kh&ocirc;ng kh&iacute; bữa tiệc th&ecirc;m s&ocirc;i động với đ&egrave;n LED nhấp nh&aacute;y đồng bộ theo nhịp điệu</h3>\r\n\r\n<p>Đ&egrave;n LED thay đổi m&agrave;u sắc theo t&acirc;m trạng người d&ugrave;ng, cho bạn thưởng thức &acirc;m nhạc h&agrave;o hứng, tưng bừng hơn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083626.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Đèn LED nhấp nháy đồng bộ theo nhịp điệu\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083626.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083626.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Đèn LED nhấp nháy đồng bộ theo nhịp điệu\" /></a></p>\r\n\r\n<h3>Kiểm so&aacute;t đ&egrave;n LED tiện lợi qua điện thoại với ứng dụng Sony |&nbsp;Music Center, Sony Fiestable&nbsp;</h3>\r\n\r\n<p>- Sony |&nbsp;Music Center cho người d&ugrave;ng dễ tạo danh s&aacute;ch bản nhạc y&ecirc;u th&iacute;ch, chuyển b&agrave;i, đổi kiểu &aacute;nh s&aacute;ng, chế độ &acirc;m thanh linh hoạt.</p>\r\n\r\n<p>-&nbsp;Sony&nbsp;Fiestable&nbsp;cho ph&eacute;p bạn điều khiển đ&egrave;n LED tr&ecirc;n loa, điều khiển chuyển động dễ d&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133520-083556.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Sony | Music Center, Sony Fiestable\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133520-083556.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133520-083556.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Sony | Music Center, Sony Fiestable\" /></a></p>\r\n\r\n<h3>Kết nối loa với&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tham khảo laptop kinh doanh tại Thế Giới Di Động\">laptop</a>,&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>,...&nbsp; với&nbsp;Bluetooth 5.0,&nbsp;NFC nhanh ch&oacute;ng</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/loa-laptop-sony\" target=\"_blank\" title=\"Loa Sony giá rẻ chính hãng bán tại Thế Giới Di Động\">Loa Sony</a>&nbsp;thiết lập dễ d&agrave;ng gh&eacute;p nối c&aacute;c thiết bị vận h&agrave;nh tr&ecirc;n nền tảng hệ điều h&agrave;nh Android, Windows,&nbsp;iOS,&nbsp;MacOS với loa bằng kết nối kh&ocirc;ng d&acirc;y Bluetooth 5.0,&nbsp;NFC ổn định.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083540.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Bluetooth 5.0, NFC\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083540.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083540.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Bluetooth 5.0, NFC\" /></a></p>\r\n\r\n<h3>C&ugrave;ng mọi người thưởng thức bản nhạc hay với&nbsp;t&iacute;nh năng&nbsp;Party Connect</h3>\r\n\r\n<p>Sử dụng t&iacute;nh năng&nbsp;Party Connect, bạn c&oacute; thể&nbsp;kết nối 100 chiếc loa&nbsp;c&ugrave;ng l&uacute;c (chỉ &aacute;p dụng với c&aacute;c d&ograve;ng SRS-XB33,&nbsp;SRS-XB23,&nbsp;SRS-XB43) th&ocirc;ng qua Bluetooth cho mọi người c&ugrave;ng h&ograve;a m&igrave;nh, nhảy chung 1 nhịp.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083603.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Party Connect\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083603.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083603.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Party Connect\" /></a></p>\r\n\r\n<h3>Sẵn s&agrave;ng sử dụng&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Tham khảo loa kinh doanh tại Thế Giới Di Động\">loa</a>&nbsp;ở mọi nơi nhờ khả năng chống bụi, chống nước chuẩn&nbsp;IP67, chống va đập tốt</h3>\r\n\r\n<p>Bạn an t&acirc;m d&ugrave;ng&nbsp;&nbsp;ở hồ bơi, b&atilde;i biển, đồi c&aacute;t, tr&ecirc;n n&uacute;i,... hoặc cầm loa lắc lư theo điệu nhạc m&agrave; kh&ocirc;ng lo loa rơi rớt sẽ giảm chất lượng &acirc;m thanh, độ bền.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083531.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Chống bụi, chống nước chuẩn IP67, chống va đập tốt\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083531.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083531.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Chống bụi, chống nước chuẩn IP67, chống va đập tốt\" /></a></p>\r\n\r\n<h3>Khi sạc đầy pin d&ugrave;ng l&acirc;u đến 24 tiếng, chọn chế độ Extra Bass d&ugrave;ng được 14 tiếng</h3>\r\n\r\n<p>Cho bạn thoải m&aacute;i trải nghiệm, kh&ocirc;ng lo hết pin.&nbsp;Sạc lại nhanh qua cổng Type-C trong 5 tiếng.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, sản phẩm c&ograve;n c&oacute; thể sạc ngược cho thiết bị kh&aacute;c qua cổng USB.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083616.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Khi sạc đầy pin dùng lâu đến 24 tiếng\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083616.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-133620-083616.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Khi sạc đầy pin dùng lâu đến 24 tiếng\" /></a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083608.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Cổng kết nối\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083608.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083608.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Cổng kết nối\" /></a></p>\r\n\r\n<h3>Tiện thao t&aacute;c với c&aacute;c n&uacute;t điều khiển r&otilde; r&agrave;ng</h3>\r\n\r\n<p>- N&uacute;t nguồn.</p>\r\n\r\n<p>- N&uacute;t kết nối Bluetooth.</p>\r\n\r\n<p>- N&uacute;t ph&aacute;t/tạm ngừng b&agrave;i h&aacute;t đồng thời l&agrave; n&uacute;t gọi rảnh tay.</p>\r\n\r\n<p>- N&uacute;t tăng/giảm &acirc;m lượng.</p>\r\n\r\n<p>- N&uacute;t Live Sound.</p>\r\n\r\n<p>- Kết nối&nbsp;NFC.</p>\r\n\r\n<p>- N&uacute;t kết nối Party Connect.</p>\r\n\r\n<p>- N&uacute;t BATT kiểm tra pin v&agrave; bật/tắt chế độ đ&egrave;n LIGHT.</p>\r\n\r\n<p>- N&uacute;t ST PAIR tạo hiệu ứng nhạc stereo tr&aacute;i - phải.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083558.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Tiện thao tác với các nút điều khiển rõ ràng\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083558.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123520-083558.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Tiện thao tác với các nút điều khiển rõ ràng\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083645.jpg\" onclick=\"return false;\"><img alt=\"Loa bluetooth Sony SRS-XB33 - Nút điều khiển\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083645.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/225613/loa-bluetooth-sony-srs-xb33-123620-083645.jpg\" title=\"Loa bluetooth Sony SRS-XB33 - Nút điều khiển\" /></a></p>\r\n\r\n<p>N&oacute;i chung, sản phẩm&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop/loa-bluetooth-sony-srs-xb33\" target=\"_blank\" title=\"Loa bluetooth Sony SRS - XB33\">loa bluetooth Sony SRS-XB33</a>&nbsp;cực đ&aacute;ng sắm nhờ những ưu điểm ấn tượng gồm thiết kế đẹp mắt, thời thượng, &acirc;m thanh tuyệt vời, nhiều tiện &iacute;ch đi k&egrave;m, dễ mang theo đến mọi nơi v&agrave; gi&aacute; cả si&ecirc;u hấp dẫn.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/loa-bluetooth-sony-srs-xb33-do-1-org.jpg', 'hot'),
(60, 17, 10, 'Loa Bluetooth Sony SRS-XE300 ', 'Loa006', 4600000, 4510000, 500, 'còn hàng', '<h3>Sở hữu thiết kế đẹp mắt, m&agrave;u sắc s&agrave;nh điệu, chất lượng &acirc;m thanh đỉnh cao,... hứa hẹn sẽ cho bạn đắm ch&igrave;m v&agrave;o kh&ocirc;ng gian &acirc;m thanh r&otilde; n&eacute;t, b&ugrave;ng nổ.</h3>\r\n\r\n<p>&bull; Được thiết kế với kiểu d&aacute;ng h&igrave;nh trụ bo tr&ograve;n tinh tế. Chất liệu nhựa v&agrave; sợi dệt tổng hợp cao cấp, đa dạng m&agrave;u sắc dễ lựa chọn theo sở th&iacute;ch.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-bluetooth\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa Bluetooth đang kinh doanh tại Thế Giới Di Động\">Loa Bluetooth</a>&nbsp;kh&ocirc;ng d&acirc;y với bộ khuếch t&aacute;n &acirc;m rộng Line-Shape độc đ&aacute;o, gi&uacute;p&nbsp;&acirc;m thanh chất lượng cao tr&agrave;n ngập kh&ocirc;ng gian. M&agrave;ng loa X-balanced hạn chế m&eacute;o tiếng khi mở lớn, gi&uacute;p tạo ra &aacute;p suất mạnh mẽ hơn cho &acirc;m thanh chất lượng cao.</p>\r\n\r\n<p>&bull;&nbsp;Đồng thời, bộ tản &acirc;m k&eacute;p thụ động gi&uacute;p c&aacute;c nốt trầm mạnh mẽ, cho kh&ocirc;ng gian &acirc;m thanh quyến rũ hơn bao giờ hết.</p>\r\n\r\n<p>&bull; C&oacute; n&uacute;t tắt mic ph&ugrave; hợp cho c&aacute;c cuộc họp, học từ xa. Thuận lợi khi nhận cuộc gọi chất lượng cao trực tiếp ngay tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa đang kinh doanh tại Thế Giới Di Động\">loa</a>&nbsp;với khả năng khử echo, kết nối đa thiết bị.&nbsp;</p>\r\n\r\n<p>&bull; Đắm m&igrave;nh v&agrave;o c&aacute;c buổi tiệc &acirc;m nhạc với thời lượng pin tới 24 giờ. Được t&iacute;ch hợp c&ocirc;ng nghệ sạc nhanh 10 ph&uacute;t = 70 ph&uacute;t nghe nhạc. Đặc biệt, c&ograve;n k&egrave;m theo chế độ bảo vệ chống chai pin cho loa.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-sony\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa Sony đang kinh doanh tại Thế Giới Di Động\">Loa Sony</a>&nbsp;c&oacute; khả năng chống sốc, chống rỉ s&eacute;t. Thoả th&iacute;ch hoạt động dưới nước với đạt chuẩn kh&aacute;ng bụi v&agrave; nước cao nhất IP67, đặc biệt c&ograve;n kh&aacute;ng được cả nước biển.</p>\r\n\r\n<p>&bull;&nbsp;Đắm ch&igrave;m v&agrave;o c&aacute;c buổi tiệc &acirc;m thanh với t&iacute;nh năng Party Connect cho ph&eacute;p kết nối kh&ocirc;ng d&acirc;y đến 100 loa.</p>\r\n\r\n<p>&bull;&nbsp;Kết nối kh&ocirc;ng d&acirc;y mượt m&agrave; trong khoảng 30 m&eacute;t với c&ocirc;ng nghệ Bluetooth 5.2.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/96w-usb-c-power-apple-mx0j2-1020x570.jpg', 'normal'),
(61, 17, 10, 'Loa Bluetooth Sony SRS-XB13', 'Loa007', 1290000, 1190000, 1000, 'còn hàng', '<h3>Kiểu d&aacute;ng di động, 2 t&ocirc;ng m&agrave;u đen - xanh dương c&aacute; t&iacute;nh</h3>\r\n\r\n<p>Thiết kế&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-sony-bluetooth\" target=\"_blank\" title=\"Loa Bluetooth Sony giá rẻ chính hãng bán tại Thế Giới Di Động\">loa Bluetooth Sony</a>&nbsp;đơn giản, gọn nhẹ chỉ<strong>&nbsp;0.34 kg,</strong>&nbsp;đi k&egrave;m&nbsp;<strong>d&acirc;y treo&nbsp;</strong>cho bạn dễ d&agrave;ng đeo v&agrave;o cổ tay của m&igrave;nh hoặc treo m&oacute;c v&agrave;o balo mang theo khi đi chơi, du lịch, đi học,... Chất liệu vỏ nhựa c&oacute; th&ecirc;m lớp<strong>&nbsp;UV coating&nbsp;</strong>cho độ bền cao, chống trầy xước, l&agrave;m sạch nhẹ nh&agrave;ng.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-6.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Kiểu dáng di động, 2 tông màu đen - xanh dương cá tính\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-6.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-6.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Kiểu dáng di động, 2 tông màu đen - xanh dương cá tính\" /></a></p>\r\n\r\n<h3>&Acirc;m thanh mạnh mẽ, lan tỏa rộng với c&ocirc;ng suất 5 W, c&ocirc;ng nghệ Extra Bass v&agrave; bộ xử l&yacute; khuếch t&aacute;n &acirc;m thanh</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/loa-laptop-bluetooth\" target=\"_blank\" title=\"Loa Blutooth chính hãng, giá tốt, bán tại Thế Giới Di Động\">Loa Bluetooth</a>&nbsp;t&iacute;ch hợp bộ tản &acirc;m thụ động phối hợp hoạt động với loa to&agrave;n dải gi&uacute;p tăng chất lượng &acirc;m trầm. C&ocirc;ng nghệ&nbsp;<strong>DSP</strong>, bộ xử l&yacute; khuếch t&aacute;n &acirc;m thanh ti&ecirc;n tiến cho &acirc;m thanh lan tỏa khắp kh&ocirc;ng gian, d&ugrave; đứng ở vị tr&iacute; n&agrave;o bạn cũng cảm nhận được sự ch&acirc;n thật trong những giai điệu y&ecirc;u th&iacute;ch.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-7.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Âm thanh mạnh mẽ, lan tỏa rộng với công suất 5 W, công nghệ Extra Bass và bộ xử lý khuếch tán âm thanh\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-7.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Âm thanh mạnh mẽ, lan tỏa rộng với công suất 5 W, công nghệ Extra Bass và bộ xử lý khuếch tán âm thanh\" /></a></p>\r\n\r\n<h3>C&oacute; mic t&iacute;ch hợp tr&ecirc;n loa gi&uacute;p bạn gọi điện rảnh tay tiện lợi</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-9.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Có mic tích hợp trên loa giúp bạn gọi điện rảnh tay tiện lợi\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-9.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-9.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Có mic tích hợp trên loa giúp bạn gọi điện rảnh tay tiện lợi\" /></a></p>\r\n\r\n<h3>Duy tr&igrave; kết nối kh&ocirc;ng d&acirc;y mượt m&agrave; xa đến 10 m nhờ c&ocirc;ng nghệ&nbsp;Bluetooth 4.2</h3>\r\n\r\n<p>Kết hợp với c&ocirc;ng nghệ&nbsp;<strong>Fast Pair&nbsp;</strong>cho khả năng kết nối kh&ocirc;ng d&acirc;y cực nhanh. Đặc biệt,&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Tham khảo loa kinh doanh tại Thế Giới Di Động\">loa</a>&nbsp;c&oacute; khả năng phối gh&eacute;p nhanh ch&oacute;ng với c&aacute;c thiết bị Android m&agrave; kh&ocirc;ng cần ch&acirc;n cắm hoặc quy tr&igrave;nh phối gh&eacute;p phức tạp, cho bạn trải nghiệm giải tr&iacute; ngay khi m&igrave;nh muốn.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-8.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Duy trì kết nối không dây mượt mà xa đến 10 m nhờ công nghệ Bluetooth 4.2\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-8.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Duy trì kết nối không dây mượt mà xa đến 10 m nhờ công nghệ Bluetooth 4.2\" /></a></p>\r\n\r\n<h3>Tạo &acirc;m thanh lớn hơn khi kết nối 2 loa&nbsp;Sony SRS-XB13 c&ugrave;ng 1 l&uacute;c</h3>\r\n\r\n<p>T&iacute;nh năng n&agrave;y của&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-sony\" target=\"_blank\" title=\"Loa Sony giá rẻ chính hãng bán tại Thế Giới Di Động\">loa Sony</a>&nbsp;chỉ hỗ trợ cho d&ograve;ng&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop/bluetooth-sony-srs-xb13\" target=\"_blank\" title=\"loa Bluetooth Sony SRS-XB13\">loa Bluetooth Sony SRS-XB13</a>, cho bạn tận hưởng kh&ocirc;ng gian &acirc;m thanh b&ugrave;ng nổ hơn. Lưu &yacute; l&agrave; khi xem phim với t&iacute;nh năng n&agrave;y, c&oacute; khả năng &acirc;m thanh v&agrave; cảnh quay kh&ocirc;ng khớp với nhau.<a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-11.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Tạo âm thanh lớn hơn khi kết nối 2 loa Sony SRS-XB13 cùng 1 lúc\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-11.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-11.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Tạo âm thanh lớn hơn khi kết nối 2 loa Sony SRS-XB13 cùng 1 lúc\" /></a></p>\r\n\r\n<h3>K&eacute;o d&agrave;i thời gian tiệc t&ugrave;ng c&ugrave;ng thời lượng pin đến khoảng 16 tiếng</h3>\r\n\r\n<p>Sạc lại trong khoảng thời gian từ<strong>&nbsp;4 - 5 tiếng&nbsp;</strong>qua cổng&nbsp;<strong>Type - C</strong>. Khi sử dụng c&ugrave;ng với smartphone, t&iacute;nh năng&nbsp;<strong>chỉ b&aacute;o pin</strong>&nbsp;sẽ gi&uacute;p bạn theo d&otilde;i ch&iacute;nh x&aacute;c lượng pin c&ograve;n lại của loa v&agrave; biết l&uacute;c n&agrave;o m&igrave;nh cần sạc lại, cực tiện lợi.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-10.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Kéo dài thời gian tiệc tùng cùng thời lượng pin đến khoảng 16 tiếng\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-10.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-10.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Kéo dài thời gian tiệc tùng cùng thời lượng pin đến khoảng 16 tiếng\" /></a></p>\r\n\r\n<h3>Đồng h&agrave;nh c&ugrave;ng bạn tr&ecirc;n mọi nẻo đường với&nbsp;khả năng chống nước - chống bụi chuẩn IP67</h3>\r\n\r\n<p>Loa chất lượng tốt từ trong ra ngo&agrave;i, chịu nước ở độ s&acirc;u tới 1 m trong 30 ph&uacute;t, bạn c&oacute; thể d&ugrave;ng ở b&atilde;i biển, hồ bơi hoặc khi đi cắm trại tiện lợi.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-12.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Đồng hành cùng bạn trên mọi nẻo đường với khả năng chống nước - chống bụi chuẩn IP67\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-12.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-12.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Đồng hành cùng bạn trên mọi nẻo đường với khả năng chống nước - chống bụi chuẩn IP67\" /></a></p>\r\n\r\n<h3>Sử dụng n&uacute;t nhấn điều chỉnh c&aacute;c chức năng loa thuận tiện</h3>\r\n\r\n<p>Ở mặt sau của loa c&oacute; t&iacute;ch hợp c&aacute;c n&uacute;t điều khiển, t&iacute;nh từ tr&aacute;i qua phải gồm c&aacute;c chức năng:</p>\r\n\r\n<p>- N&uacute;t nguồn bật/tắt loa.</p>\r\n\r\n<p>- N&uacute;t bật/tắt kết nối Bluetooth.&nbsp;</p>\r\n\r\n<p>- N&uacute;t ph&aacute;t/dừng chơi nhạc đồng thời l&agrave; n&uacute;t nghe/nhận cuộc gọi.</p>\r\n\r\n<p>- N&uacute;t giảm &acirc;m lượng.</p>\r\n\r\n<p>- N&uacute;t tăng &acirc;m lượng.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-13.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB13 - Sử dụng nút nhấn điều chỉnh các chức năng loa thuận tiện\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-13.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/249767/bluetooth-sony-srs-xb13-13.jpg\" title=\"Loa Bluetooth Sony SRS-XB13 - Sử dụng nút nhấn điều chỉnh các chức năng loa thuận tiện\" /></a></p>\r\n\r\n<p>Nổi bật với kiểu d&aacute;ng nhỏ gọn, &acirc;m thanh sinh động nhờ&nbsp;c&ocirc;ng suất 5 W, c&ocirc;ng nghệ Extra Bass, bộ xử l&yacute; khuếch t&aacute;n &acirc;m thanh, kết nối kh&ocirc;ng d&acirc;y&nbsp;Bluetooth 4.2, thời lượng pin đến 16 tiếng, chuẩn chống nước - chống bụi IP67, Sony SRS-XB13 chắc chắn l&agrave; 1 mảnh gh&eacute;p ho&agrave;n hảo để cuộc sống đầy sắc m&agrave;u của bạn th&ecirc;m tuyệt vời hơn.&nbsp;</p>\r\n', 'admin', '01/12/2022', 'public/uploads/bluetooth-sony-srs-xb13-1-1-org.jpg', 'normal'),
(62, 17, 10, 'Loa Bluetooth Sony Extra Bass SRS-XB01', 'Loa008', 800000, 640000, 500, 'còn hàng', '<h3>Thiết kế&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-bluetooth\" target=\"_blank\" title=\"Xem thêm các loa Bluetooth đang bán tại Thế Giới Di Động\">Loa Bluetooth</a>&nbsp;Sony SRS-XB01 nhỏ gọn, tiện lợi mang theo trong buổi d&atilde; ngoại hoặc đi du lịch</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-1.jpg\" onclick=\"return false;\"><img alt=\"Kích thước Loa Bluetooth Sony SRS-XB01\" data-src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-1.jpg\" title=\"Kích thước Loa Bluetooth Sony SRS-XB01\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/loa-laptop/loa-bluetooth-sony-srs-xb01\" target=\"_blank\" title=\"Tham khảo Loa Bluetooth Sony SRS-XB01 kinh doanh tại Thế Giới Di Động\">Loa Bluetooth Sony SRS-XB01</a>&nbsp;được trang bị d&acirc;y đeo tiện lợi</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-2.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB01 có dây đeo tiện lợi\" data-src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-2.jpg\" title=\"Loa Bluetooth Sony SRS-XB01 có dây đeo tiện lợi\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/loa-laptop-sony\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa Sony giá rẻ chính hãng bán tại Thế Giới Di Động\">Loa Sony</a>&nbsp;t&iacute;ch hợp c&ocirc;ng nghệ Extra Bass độc quyền&nbsp;cho &acirc;m thanh b&ugrave;ng nổ v&agrave; s&ocirc;i động hơn với chất &acirc;m được ph&aacute;t ra ở mặt trước của loa</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01.gif\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB01 tích hợp công nghệ Extra Bass cho âm thanh sống động\" data-src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01.gif\" src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01.gif\" title=\"Loa Bluetooth Sony SRS-XB01 tích hợp công nghệ Extra Bass cho âm thanh sống động\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/loa-laptop-sony-bluetooth\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa Bluetooth Sony giá rẻ chính hãng bán tại Thế Giới Di Động\">Loa Bluetooth Sony</a>&nbsp;nghe nhạc đến 6 giờ qua Bluetooth 4.2 hoặc jack cắm 3.5 mm kết nối với&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo các sản phẩm điện thoại đang kinh doanh tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo các sản phẩm máy tính bảng đang kinh doanh tại Thế Giới Di Động\">m&aacute;y t&iacute;nh bảng</a>,&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Tham khảo các sản phẩm laptop đang kinh doanh tại Thế Giới Di Động\">laptop</a></h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-4.jpg\" onclick=\"return false;\"><img alt=\"Khả năng sử dụng của Loa Bluetooth Sony SRS-XB01\" data-src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-4.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-4.jpg\" title=\"Khả năng sử dụng của Loa Bluetooth Sony SRS-XB01\" /></a></p>\r\n\r\n<p><strong>Lưu &yacute;:</strong>&nbsp;Khi pin gần hết&nbsp;v&agrave; b&aacute;o pin yếu th&igrave; l&uacute;c n&agrave;y loa sẽ tự về&nbsp;chế độ&nbsp;<b>tiết kiệm pin</b>&nbsp;cho ph&eacute;p người d&ugrave;ng nghe nhạc th&ecirc;m từ&nbsp;15 - 20 ph&uacute;t&nbsp;với mức &acirc;m lượng nhỏ v&agrave; kh&ocirc;ng mở lớn được.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/2162/194248/loa-bluetooth-sony-srs-xb01-5.jpg\" onclick=\"return false;\"><img alt=\"Các cách sử dụng trên Loa Bluetooth Sony SRS-XB01\" data-src=\"https://cdn.tgdd.vn/Products/Images/2162/194248/loa-bluetooth-sony-srs-xb01-5.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/2162/194248/loa-bluetooth-sony-srs-xb01-5.jpg\" title=\"Các cách sử dụng trên Loa Bluetooth Sony SRS-XB01\" /></a></p>\r\n\r\n<h3>Kh&aacute;ng nước chuẩn IPX5 đảm bảo an to&agrave;n cho&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Tham khảo các sản phẩm loa đang kinh doanh tại Thế Giới Di Động\">loa nghe nhạc</a>&nbsp;khi gặp mưa hoặc bị nước đổ l&ecirc;n</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-7.jpg\" onclick=\"return false;\"><img alt=\"Khả năng kháng nước của Loa Bluetooth Sony SRS-XB01\" data-src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-7.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-7.jpg\" title=\"Khả năng kháng nước của Loa Bluetooth Sony SRS-XB01\" /></a></p>\r\n\r\n<h3>T&iacute;nh năng của c&aacute;c n&uacute;t nhấn v&agrave; cổng kết nối tr&ecirc;n loa</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-8.jpg\" onclick=\"return false;\"><img alt=\"Các nút trên Loa Bluetooth Sony SRS-XB01\" data-src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-8.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-8.jpg\" title=\"Các nút trên Loa Bluetooth Sony SRS-XB01\" /></a><a href=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-fixx1.jpg\" onclick=\"return false;\"><img alt=\"Loa Bluetooth Sony SRS-XB01\" data-src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-fixx1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/382/194248/loa-bluetooth-sony-srs-xb01-fixx1.jpg\" title=\"Loa Bluetooth Sony SRS-XB01\" /></a></p>\r\n', 'admin', '01/12/2022', 'public/uploads/loa-bluetooth-sony-extra-bass-srs-xb01-2.jpg', 'normal'),
(63, 17, 10, 'Loa Bluetooth Sony SRS-XE200', 'Loa009', 2950000, 2800000, 500, 'còn hàng', '<h3>Với thiết kế gọn g&agrave;ng, t&iacute;nh di động cao, &acirc;m thanh mạnh mẽ,... hứa hẹn sẽ mang lại trải nghiệm ấn tượng cho bạn.</h3>\r\n\r\n<p>&bull;&nbsp;Thiết kế với kiểu d&aacute;ng h&igrave;nh trụ bo tr&ograve;n đẹp mắt, t&iacute;nh di động cao, c&oacute; d&acirc;y đeo đi k&egrave;m tiện lợi. Chất liệu nhựa v&agrave; sợi dệt tổng hợp cao cấp, đa dạng m&agrave;u sắc cho bạn lựa chọn.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-bluetooth\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa Bluetooth đang kinh doanh tại Thế Giới Di Động\">Loa Bluetooth</a>&nbsp;kh&ocirc;ng d&acirc;y với bộ khuếch t&aacute;n &acirc;m rộng Line-Shape độc đ&aacute;o cho &acirc;m thanh lan tỏa rộng khắp kh&ocirc;ng gian. M&agrave;ng loa X-balanced hạn chế m&eacute;o tiếng khi mở lớn bộ tản &acirc;m k&eacute;p thụ động cho &acirc;m thanh mạnh mẽ.</p>\r\n\r\n<p>&bull;&nbsp;Hỗ trợ nhận cuộc gọi chất lượng cao ngay tr&ecirc;n&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa đang kinh doanh tại Thế Giới Di Động\">loa</a>&nbsp;với khả năng khử echo, kết nối đa thiết bị. C&oacute; n&uacute;t tắt mic ph&ugrave; hợp cho họp, học từ xa.</p>\r\n\r\n<p>&bull;&nbsp;Nghe nhạc thoải m&aacute;i với thời lượng pin tới 16 giờ. C&ocirc;ng nghệ sạc nhanh 10 ph&uacute;t = 70 ph&uacute;t nghe nhạc k&egrave;m chế độ bảo vệ chống chai pin.</p>\r\n\r\n<p>&bull;&nbsp;<a href=\"https://www.thegioididong.com/loa-laptop-sony\" target=\"_blank\" title=\"Xem thêm các sản phẩm loa Sony đang kinh doanh tại Thế Giới Di Động\">Loa Sony</a>&nbsp;c&oacute; khả năng chống sốc, chống rỉ s&eacute;t. Đạt chuẩn kh&aacute;ng bụi v&agrave; nước cao nhất IP67, đặc biệt kh&aacute;ng được cả nước biển.</p>\r\n\r\n<p>&bull;&nbsp;Đắm ch&igrave;m v&agrave;o c&aacute;c buổi tiệc &acirc;m thanh với t&iacute;nh năng Party Connect cho ph&eacute;p kết nối kh&ocirc;ng d&acirc;y đến 100 loa.</p>\r\n\r\n<p>&bull;&nbsp;Kết nối kh&ocirc;ng d&acirc;y ổn định trong khoảng 30 m&eacute;t với c&ocirc;ng nghệ Bluetooth 5.2.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/loa-bluetooth-sony-srs-xe200-den-1-1.jpg', 'hot'),
(64, 18, 8, 'Pin sạc dự phòng 7500 mAh AVA DS004 ', 'SDP001', 60000, 350000, 1000, 'còn hàng', '<h3>Thiết kế nhỏ gọn, cứng c&aacute;p, 2 tone m&agrave;u đen v&agrave; hồng thời trang</h3>\r\n\r\n<p>Vỏ bọc bền tốt, hạn chế b&aacute;m bẩn, c&aacute;c g&oacute;c bo tr&ograve;n gi&uacute;p bảo vệ linh kiện b&ecirc;n trong tối ưu khi xảy ra sự cố. K&iacute;ch thước sạc dự ph&ograve;ng vừa vặn trong tầm tay bạn, c&oacute; thể cất v&agrave;o t&uacute;i quần, t&uacute;i x&aacute;ch, mang theo nhẹ nh&agrave;ng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080209.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS004 - Thiết kế nhỏ gọn, cứng cáp\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080209.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080209.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS004 - Thiết kế nhỏ gọn, cứng cáp\" /></a></p>\r\n\r\n<h3>Nạp pin được nhiều lần cho c&aacute;c thiết bị qua&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd?g=7500-mah\" target=\"_blank\" title=\"pin dự phòng có dung lượng 7.500 mAh chính hãng, giá rẻ, bán tại Thế Giới Di Động\">dung lượng sạc dự ph&ograve;ng 7.500 mAh</a>&nbsp;</h3>\r\n\r\n<p>Sạc dự ph&ograve;ng l&agrave;m việc được với mọi&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng kinh doanh tại Thế Giới Di Động\">tablet</a>,&nbsp;tai nghe Bluetooth tương th&iacute;ch v&agrave; t&ugrave;y theo từng thiết bị m&agrave; số lần sạc được sẽ kh&aacute;c nhau. Với iPhone 11 Pro Max th&igrave; sạc dự ph&ograve;ng n&agrave;y c&oacute; thể sạc đầy 1.2 lần.</p>\r\n\r\n<p>Hiệu suất sạc đến 64% hứa hẹn sẽ cung cấp nguồn năng lượng nhanh ch&oacute;ng, cho thiết bị của bạn sẵn s&agrave;ng đ&aacute;p ứng nhu cầu mọi l&uacute;c bạn cần.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080221.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS004 - Nạp pin được nhiều lần cho các thiết bị qua dung lượng sạc dự phòng 7.500 mAh \" data-src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080221.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080221.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS004 - Nạp pin được nhiều lần cho các thiết bị qua dung lượng sạc dự phòng 7.500 mAh \" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/sac-dtdd-li-ion\" target=\"_blank\" title=\"Sạc dự phòng có lõi pin Li-Ion giá rẻ chính hãng bán tại Thế Giới Di Động\">L&otilde;i pin Li-Ion</a>&nbsp;lưu trữ năng lượng tốt, hạn chế thất tho&aacute;t d&ograve;ng điện, d&ugrave;ng an to&agrave;n&nbsp;</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080227.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS004 - Lõi pin Li-Ion lưu trữ năng lượng tốt\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080227.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080227.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS004 - Lõi pin Li-Ion lưu trữ năng lượng tốt\" /></a></p>\r\n\r\n<h3>Hỗ trợ 1 cổng sạc Micro USB v&agrave;o v&agrave; 2 cổng sạc ra&nbsp;USB</h3>\r\n\r\n<p>Cả nguồn v&agrave;o v&agrave; nguồn ra đều&nbsp;c&oacute; d&ograve;ng điện 2A,&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\" title=\"Pin sạc dự phòng chính hãng, giá rẻ, bán tại Thế Giới Di Động\">sạc dự ph&ograve;ng</a>&nbsp;c&oacute; khả năng nạp pin cho ch&iacute;nh m&igrave;nh v&agrave; thiết bị với tốc độ cao. Đặc biệt, bạn c&oacute; thể sử dụng được 2 cổng USB đồng thời&nbsp;để nạp pin cho 2 thiết bị trong 1 lần, gi&uacute;p r&uacute;t ngắn thời gian chờ đợi tối đa.</p>\r\n\r\n<p>B&ecirc;n dưới cổng v&agrave;o c&oacute; 4 đ&egrave;n LED b&aacute;o hiệu dung lượng pin của sản phẩm, với mỗi đ&egrave;n thể hiện cho 25% pin.&nbsp;</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190820-080855.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS004 - Hỗ trợ 1 cổng sạc Micro USB vào và 2 cổng sạc ra USB\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190820-080855.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190820-080855.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS004 - Hỗ trợ 1 cổng sạc Micro USB vào và 2 cổng sạc ra USB\" /></a></p>\r\n\r\n<p>Lưu &yacute;, khi sạc lại cho&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd/pin-sac-du-phong-7500mah-ava-ds004\" target=\"_blank\" title=\"pin sạc dự phòng 7500mAh AVA DS004\">pin sạc dự ph&ograve;ng 7500mAh AVA DS004</a>&nbsp;nếu d&ugrave;ng adapter 1A th&igrave; thời gian sẽ tầm 7 - 8 giờ c&ograve;n d&ugrave;ng adapter 2A, thời gian sẽ nhanh hơn chỉ trong 3 - 4 giờ.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080232.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS004 - Thời gian sạc lại pin cho sạc dự phòng\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080232.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/232353/pin-sac-du-phong-7500mah-ava-ds004-190220-080232.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS004 - Thời gian sạc lại pin cho sạc dự phòng\" /></a></p>\r\n\r\n<p>T&oacute;m lại, pin sạc dự ph&ograve;ng AVA DS004 l&agrave; một t&iacute;ch trữ năng lượng tiện dụng rất đ&aacute;ng để bạn lựa chọn đồng h&agrave;nh trong cuộc sống thường nhật hoặc khi thực hiện c&aacute;c chuyến h&agrave;nh tr&igrave;nh d&agrave;i, sản phẩm sẽ hỗ trợ bạn trải nghiệm cuộc sống th&ecirc;m thoải m&aacute;i hơn.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/pin-sac-du-phong-7500mah-ava-ds004-den-1-org.jpg', 'hot'),
(65, 18, 8, 'Pin sạc dự phòng 7500 mAh AVA DS630', 'SDP002', 300000, 150000, 1000, 'còn hàng', '<h3>Bề ngo&agrave;i tinh tế, gọn nhẹ chỉ 181 g, c&oacute; 2 m&agrave;u xanh Navy v&agrave; đen đẹp mắt</h3>\r\n\r\n<p>Như hầu hết c&aacute;c sản phẩm&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd-ava\" target=\"_blank\" title=\"Pin sạc dự phòng AVA chính hãng đang bán tại Thế Giới Di Động\">sạc dự ph&ograve;ng AVA</a>&nbsp;kh&aacute;c, mẫu&nbsp;AVA DS630 cũng sở hữu thiết kế hiện đại, nhỏ gọn, kh&ocirc;ng chiếm diện t&iacute;ch trong t&uacute;i x&aacute;ch, balo của bạn m&agrave; ngay cả khi bạn cầm trực tiếp tr&ecirc;n tay để di chuyển, sử dụng cũng vẫn cảm thấy rất thoải m&aacute;i, dễ chịu.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090720-040750.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS630 - Bề ngoài tinh tế, gọn nhẹ\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090720-040750.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090720-040750.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS630 - Bề ngoài tinh tế, gọn nhẹ\" /></a></p>\r\n\r\n<h3>Nạp pin nhanh ch&oacute;ng với 2 cổng USB d&ograve;ng sạc&nbsp;5V - 2.1A</h3>\r\n\r\n<p><a href=\"https://www.thegioididong.com/sac-dtdd/pin-sac-du-phong-7500mah-ava-ds630\" target=\"_blank\" title=\"Pin sạc dự phòng 7500mAh AVA DS630\">Pin sạc dự ph&ograve;ng 7500mAh AVA DS630</a>&nbsp;hỗ trợ chuẩn kết nối USB quen thuộc đ&aacute;p ứng d&ograve;ng sạc mạnh mẽ, cho bạn sạc 2 thiết bị 1 lần thuận tiện, kh&ocirc;ng mất thời gian chờ đợi l&acirc;u lại c&ograve;n c&oacute; thể chia sẻ sạc dự ph&ograve;ng với mọi người dễ d&agrave;ng.</p>\r\n\r\n<p>Theo d&otilde;i lượng pin đơn giản qua dải đ&egrave;n LED với mỗi đ&egrave;n tương ứng 25% pin.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090720-040757.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS630 - Nạp pin nhanh chóng với 2 cổng USB dòng sạc 5V - 2.1A\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090720-040757.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090720-040757.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS630 - Nạp pin nhanh chóng với 2 cổng USB dòng sạc 5V - 2.1A\" /></a></p>\r\n\r\n<h3>Hiệu suất sạc cao 64% với&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd?g=7500-mah\" target=\"_blank\" title=\"pin dự phòng có dung lượng 7.500 mAh chính hãng, giá rẻ, bán tại Thế Giới Di Động\">dung lượng sạc dự ph&ograve;ng 7.500 mAh</a>&nbsp;</h3>\r\n\r\n<p>C&oacute; thể sạc cho iPhone 11 Pro Max khoảng 1.2 lần, cung cấp năng lượng cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd\" target=\"_blank\" title=\"Tham khảo điện thoại kinh doanh tại Thế Giới Di Động\">điện thoại</a>,&nbsp;<a href=\"https://www.thegioididong.com/may-tinh-bang\" target=\"_blank\" title=\"Tham khảo máy tính bảng kinh doanh tại Thế Giới Di Động\">tablet</a>&nbsp;của bạn nhanh ch&oacute;ng mọi l&uacute;c, tha hồ tận hưởng cuộc sống theo c&aacute;ch bạn muốn v&agrave; qu&ecirc;n lu&ocirc;n nỗi lo thiết bị hết pin, hết khả năng ghi nhận c&aacute;c h&igrave;nh ảnh, video đẹp hoặc bỏ lỡ email quan trọng.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040804.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS630 - Dung lượng sạc dự phòng 7.500 mAh\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040804.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040804.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS630 - Dung lượng sạc dự phòng 7.500 mAh\" /></a></p>\r\n\r\n<h3>T&iacute;ch hợp&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd-li-ion\" target=\"_blank\" title=\"Sạc dự phòng có lõi pin Li-ion giá rẻ chính hãng bán tại Thế Giới Di Động\">l&otilde;i pin Li-ion</a>&nbsp;lưu trữ năng lượng cao, sử dụng an to&agrave;n cho người d&ugrave;ng v&agrave; điện thoại</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040811.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS630 - Tích hợp lõi pin Li-ion lưu trữ năng lượng cao\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040811.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040811.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS630 - Tích hợp lõi pin Li-ion lưu trữ năng lượng cao\" /></a></p>\r\n\r\n<h3>Sạc lại qua cổng Micro USB 5V - 2A chỉ trong 5 - 6 giờ khi d&ugrave;ng adapter 2A&nbsp;</h3>\r\n\r\n<p>Trường hợp, nếu bạn sử dụng adapter 1A th&igrave; cần từ 10 - 11 giờ để&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\" title=\"Pin sạc dự phòng chính hãng, giá rẻ, bán tại Thế Giới Di Động\">sạc dự ph&ograve;ng</a>&nbsp;đầy năng lượng, t&ugrave;y chọn adapter ph&ugrave; hợp để nạp pin hiệu quả.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040817.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7500mAh AVA DS630 - Sạc lại qua cổng Micro USB 5V - 2A chỉ trong 5 - 6 giờ\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040817.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/230661/pin-sac-du-phong-7500mah-ava-ds630-090820-040817.jpg\" title=\"Pin sạc dự phòng 7500mAh AVA DS630 - Sạc lại qua cổng Micro USB 5V - 2A chỉ trong 5 - 6 giờ\" /></a></p>\r\n\r\n<p>Một mẫu sạc dự ph&ograve;ng vừa nhỏ gọn, đẹp mắt vừa d&ugrave;ng bền, sạc nhanh v&agrave; gi&aacute; cả hấp dẫn như AVA DS630, thương hiệu của Thế Giới Di Động thật đ&aacute;ng &quot;động t&acirc;m&quot; bạn nhỉ, nhanh tay tậu 1 em để đảm bảo nguồn năng lượng dồi d&agrave;o cho c&aacute;c thiết bị y&ecirc;u th&iacute;ch của bạn ngay.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/pin-sac-du-phong-7500mah-ava-ds630-xanh-navy-1-org.jpg', 'discount');
INSERT INTO `tbl_product` (`id`, `id_category`, `id_brand`, `name`, `code`, `price`, `promotional_price`, `quantity`, `status`, `description`, `user`, `create_date`, `image`, `level`) VALUES
(66, 18, 8, 'Pin sạc dự phòng 10.000 mAh AVA LA Y65', 'SDP003', 600000, 400000, 500, 'còn hàng', '<h3>Với k&iacute;ch thước nhỏ gọn dễ mang theo, thiết kế tinh tế đẹp mắt</h3>\r\n\r\n<p>Bạn dễ d&agrave;ng đặt n&oacute; trong balo, t&uacute;i x&aacute;ch khi đi l&agrave;m, đi du lịch,... thật gọn g&agrave;ng v&agrave; tiện lợi. M&agrave;u v&agrave;ng v&agrave; đen thời trang, ph&ugrave; hợp với cả bạn nam v&agrave; nữ. Vỏ sạc được l&agrave;m bằng nhựa nh&aacute;m kh&ocirc;ng bị b&aacute;m v&acirc;n tay hay trơn trượt&nbsp; mồ h&ocirc;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035213.jpg\" onclick=\"return false;\"><img alt=\"Nhỏ gọn, tiện dụng - Pin sạc dự phòng 10.000mAh AVA LA Y65\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035213.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035213.jpg\" title=\"Nhỏ gọn, tiện dụng - Pin sạc dự phòng 10.000mAh AVA LA Y65\" /></a></p>\r\n\r\n<h3>Dung lượng pin lớn&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd?g=10000-mah\" target=\"_blank\" title=\"Pin sạc dự phòng dung lượng pin 10.000 mAh giá tốt bán tại Thế Giới Di Động\">10.000 mAh</a>, hiệu suất sạc cao tới 65%</h3>\r\n\r\n<p>Sử dụng cho 1.6 lần sạc&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-11-pro-max-512gb\" target=\"_blank\" title=\"Điện thoại iPhone 11 Pro Max 512GB giá rẻ chính hãng bán tại Thế Giới Di Động\">iPhone 11 Pro Max</a>,&nbsp;2 lần cho&nbsp;<a href=\"https://www.thegioididong.com/dtdd/iphone-xs-max-256gb\" target=\"_blank\" title=\"Điện thoại iPhone Xs Max 256GB giá rẻ chính hãng bán tại Thế Giới Di Động\">iPhone Xs Max</a>, đảm bảo&nbsp;nguồn năng lượng dự ph&ograve;ng dồi d&agrave;o cho&nbsp;thiết bị di động của bạn hoạt động hiệu quả trong suốt h&agrave;nh tr&igrave;nh d&agrave;i.</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035217.jpg\" onclick=\"return false;\"><img alt=\"Dung lượng lớn - Pin sạc dự phòng 10.000mAh AVA LA Y65\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035217.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035217.jpg\" title=\"Dung lượng lớn - Pin sạc dự phòng 10.000mAh AVA LA Y65\" /></a></p>\r\n\r\n<h3>L&otilde;i pin Li-Ion tuổi thọ cao, trang bị 4 đ&egrave;n led b&aacute;o hiệu dung lượng pin tiện theo d&otilde;i</h3>\r\n\r\n<p>Xem th&ecirc;m:&nbsp;<a href=\"https://www.thegioididong.com/hoi-dap/so-sanh-pin-li-ion-va-pin-li-po-651833\" target=\"_blank\" title=\"So sánh Pin li-po và Pin Li-ion\">Pin li-po l&agrave; g&igrave;? Pin Li-Ion l&agrave; g&igrave;? Ưu v&agrave; nhược điểm?</a></p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035222.jpg\" onclick=\"return false;\"><img alt=\"Đèn led báo dung lượng - Pin sạc dự phòng 10.000mAh AVA LA Y65\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035222.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035222.jpg\" title=\"Đèn led báo dung lượng - Pin sạc dự phòng 10.000mAh AVA LA Y65\" /></a></p>\r\n\r\n<h3><a href=\"https://www.thegioididong.com/sac-dtdd\" target=\"_blank\" title=\"Sạc dự phòng giá rẻ chính hãng bán tại Thế Giới Di Động\">Sạc dự ph&ograve;ng</a>&nbsp;thiết kế tiện lợi với&nbsp;2 cổng sạc ra USB v&agrave; cổng sạc v&agrave;o Micro USB</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035227.jpg\" onclick=\"return false;\"><img alt=\"Cổng sạc tiện dụng - Pin sạc dự phòng 10.000mAh AVA LA Y65\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035227.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035227.jpg\" title=\"Cổng sạc tiện dụng - Pin sạc dự phòng 10.000mAh AVA LA Y65\" /></a></p>\r\n\r\n<h3>D&ugrave;ng được với 2 thiết bị c&ugrave;ng l&uacute;c, gọn g&agrave;ng v&agrave; tiết kiệm thời gian</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035232.jpg\" onclick=\"return false;\"><img alt=\"Sạc 2 thiết bị cùng lúc - Pin sạc dự phòng 10.000mAh AVA LA Y65\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035232.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035232.jpg\" title=\"Sạc 2 thiết bị cùng lúc - Pin sạc dự phòng 10.000mAh AVA LA Y65\" /></a></p>\r\n\r\n<h3>Nạp lại năng lượng cho pin sạc đơn giản qua&nbsp;<a href=\"https://www.thegioididong.com/cap-dien-thoai\" target=\"_blank\" title=\"Adapter sạc giá rẻ chính hãng bán tại Thế Giới Di Động\">adapter sạc</a>&nbsp;hoặc sạc qua&nbsp;<a href=\"https://www.thegioididong.com/laptop\" target=\"_blank\" title=\"Laptop giá rẻ chính hãng bán tại Thế Giới Di Động\">laptop</a></h3>\r\n\r\n<p>Thời gian sạc đầy pin: 10 - 11 giờ (d&ugrave;ng Adapter 1A), 5 - 6 giờ (d&ugrave;ng Adapter 2A).</p>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035238.jpg\" onclick=\"return false;\"><img alt=\"Nạp năng lượng đơn giản - Pin sạc dự phòng 10.000mAh AVA LA Y65\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035238.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/224988/pin-sac-du-phong-10000mah-ava-la-y65-015220-035238.jpg\" title=\"Nạp năng lượng đơn giản - Pin sạc dự phòng 10.000mAh AVA LA Y65\" /></a></p>\r\n\r\n<p>C&oacute; thể thấy rằng,&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd/pin-sac-du-phong-10000mah-ava-la-y65\" target=\"_blank\" title=\"Pin sạc dự phòng 10.000mAh AVA LA Y65\">pin sạc dự ph&ograve;ng 10.000mAh AVA LA Y65</a>&nbsp;l&agrave; nguồn năng lượng dự ph&ograve;ng gọn nhẹ, tiện lợi đầy linh động m&agrave; người bận rộn n&ecirc;n lựa chọn l&uacute;c n&agrave;y.</p>\r\n', 'admin', '01/12/2022', 'public/uploads/pin-sac-du-phong-10000mah-ava-la-y65-190820-0853350.jpg', 'normal'),
(67, 18, 8, 'Pin sạc dự phòng 7.500 mAh AVA DS630-WB', 'SDP004', 500000, 300000, 500, 'hết hàng', '<h3>C&oacute; thiết kế gọn g&agrave;ng, dễ d&agrave;ng mang theo, kh&ocirc;ng lo thiết bị hết pin khi đang ở b&ecirc;n ngo&agrave;i</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang1.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh AVA DS630-WB Trắng có thiết kế nhỏ gọn\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang1.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang1.jpg\" title=\"Pin sạc dự phòng 7.500 mAh AVA DS630-WB trắng có thiết kế nhỏ gọn\" /></a></p>\r\n\r\n<h3>Trang bị l&otilde;i pin Li-Ion c&oacute; tuổi thọ cao</h3>\r\n\r\n<h3><a href=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang2.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh AVA DS630-WB Trắng với lõi pin an toàn\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang2.jpg\" src=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang2.jpg\" title=\"Pin sạc dự phòng 7.500 mAh AVA DS630-WB trắng với lõi pin an toàn\" /></a></h3>\r\n\r\n<h3>Với&nbsp;<a href=\"https://www.thegioididong.com/sac-dtdd-7500mah\" target=\"_blank\" title=\"Pin sạc dự phòng có dung lượng pin 7.500 mAh giá tốt bán tại Thế Giới Di Động\">dung lượng 7.500 mAh</a>, c&oacute; thể sạc được cho nhiều thiết bị như điện thoại, m&aacute;y t&iacute;nh bảng...</h3>\r\n\r\n<p><a href=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang3.jpg\" onclick=\"return false;\"><img alt=\"Pin sạc dự phòng 7.500 mAh AVA DS630-WB Trắng tương thích với nhiều thiết bị\" data-src=\"https://cdn.tgdd.vn/Products/Images/57/204169/pin-sac-du-phong-7500mah-ava-ds630-wb-trang3.jpg\" src=\"